INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("135", "25", "1", "三星R208", "1080", "年轻人的最佳选择R208", "尺寸：110 X 46 X 23.5 mm <br>\r\n重量：99 g <br>\r\n通话时间：3.5 小时 <br>\r\n待机时间：120 小时 <br>\r\n屏幕尺寸：128 X 64 pixels <br>\r\n配置：二电一充<br>\r\n<br>\r\n<br>\r\n有别于之前推出的折叠式(folder-type)或掀盖式(flip-type)手机，三星SGH-R208“蓝极光”是第一款采握把式(bar-type)的手机，另外，在造型设计上，由于配备蓝色冷光屏幕面板，在黑暗中显得格外眩目明亮。昵称为“蓝极光”，主要是因为屏幕面板散发的蓝色冷光，彷佛就像宇宙高能量电子沿着行星磁场高速行进入上层大气的电子蓝极光现象。 <br>\r\n<br>\r\n主打年轻市场的 R208，高分辨率 LCD 屏幕，重量为99克，先推出海蓝及深灰两种颜色，在功能造型上，R208 除了配备蓝色冷光屏幕面板外，较特别的是它的收讯显示 (LED)有五种不同颜色：绿、橙、紫、红、蓝，同时可以设定用来区分不同来电者以及短讯。R208也包括了个人数据管理功能，例如行事历，世界时间和计算器等。这只手机也支持声控拨号、声控指令、话音备忘，及电话录音机等功能。针对年轻消费族群，特别提供 200 个超大电话簿，并有 30 组铃声选择，及支持铃声下载、自编铃声和来电分组等功能，还有 5 组内建动画屏幕保护程序及 11 组游戏，让年轻学生更能轻松拥有高品质的流行机型。", "0", "0", "2002-12-06 00:00:00", "pic/r208.jpg", "不详", "1080", "1800", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("136", "25", "1", "三星A288", "1900", "SGH-A288总是透著一份纯净自然的质感，而令人痴迷的外表下却蕴涵着极富激情的机能，双屏蓝眼机可以说是一网打尽市面上手机的杀手级功能。", "内建WAP1.1版浏览器 <br>\n声控上网、声控操作、声控拨号 <br>\n五组来电分组功能 <br>\n三组自编铃声、铃声下载 <br>\n电话记忆容量200 组，每组电话簿可储存6项记录 <br>\n20组声控拨号 <br>\n蓝色冷光液晶显示萤幕 <br>\n颜色：珍珠白、金色 <br>\nGSM900/1800 <br>\n长、宽、厚：79x38x23mm <br>\n重量：77克(含电池) <br>\n通话时间：薄电池 120 分钟、标准电池 180 分钟 <br>\n待机时间：薄电池 60 小时、标准电池 90 小时 <br>\n配置：二电一充<br>\n<br>\n<br>\nSGH-A288最大的特点就在于话盖上镶金边的圆形蓝色冷光液晶显示屏幕，尺寸为64X64像素，这样就避免了贝壳机的弊端，您不必打开话盖就能掌握时间、来电号码、收讯情况、电池用量以及来电震动开关等实用讯息。掀开话盖，内部则是另一个三星标准的浅绿色冷光液晶的主屏幕，128X64像素解析度，一次可以同时显示四行文字及一行图示。 <br>\n<br>\nSGH-A288总是透著一份纯净自然的质感，而令人痴迷的外表下却蕴涵着极富激情的机能，双屏蓝眼机可以说是一网打尽市面上手机的杀手级功能。 <br>\n<br>\nSGH-A288清晰简便的四向功能向导键，只需单手即可以完全操纵。不管是控制手机功能、拨号通话或是上网流览，四向功能键的特殊柔软舒适的材质都将给你特殊的体贴。", "0", "0", "2002-12-06 00:00:00", "pic/a288.jpg", "不详", "1850", "3000", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("137", "25", "1", "三星N188", "1050", "商务人士的选择N188", "网络类型：GSM900/GSM1800 <br>\r\n重量：83克 <br>\r\n尺寸/体积：150x42x17.5mm <br>\r\n通话/待机时间：210分钟/90小时 <br>\r\n内置WAP1.1浏览器 <br>\r\n声控菜单功能，包括WAP <br>\r\n20个声控拨号/20个语音记事/20个语音指令 <br>\r\n免提耳机（可配合语音指令） <br>\r\n内置振动、屏保动画 <br>\r\nT9中英文输入法（支持电话簿，短讯，铃声名称） <br>\r\n三组自编铃声、来电铃声分组 <br>\r\n内置11个游戏 <br>\r\n个人信息管理 <br>\r\n（日程表、计算器、闹钟、世界时钟、日历、记事本） <br>\r\n配置：二电一充<br>\r\n<br>\r\n<br>\r\nN188的机身非常轻巧，机身重量只有83克，体积也只有105x42x17.5mm，是目前三星手机中最轻的。N188机身正面线条比2488要柔和一些，同时还在听筒和翻盖上分别加上了做点缀用的亮色装饰线，使整部手机彻底摆脱了2488那种呆板的设计，显得活泼而自然；机身侧面则和2488几乎相同，略薄。N188共有碳灰，银白，鹦鹉蓝，香槟金四种色彩选择。 <br>\r\n<br>\r\n依旧是三星风格的绿色屏幕，但这次三星不忘加入了流行的的屏保，共有5款有趣屏保图象，分别是山河、花瓶、林中鸟、蚂蚁以及月夜，更是顺应了目前崇尚自然的潮流。 <br>\r\n<br>\r\n其实N188给人最大的惊喜便是酷味十足的键盘按钮设计。虽然按键的布局还是沿用传统方式，但是按键形状是比较少见的梯形，完全是有棱有角的金属味道，而且这些按键几乎连成一片。这样的设计使整个键盘按钮比普通手机要大不少，使用感觉非常方便。当然，这也是考虑到N188主要的消费群体是成功商务人士。 <br>\r\n<br>\r\n在功能方面，N188作为三星首款WAP中文手机内置了WAP1.1浏览器，通过WAP可登陆各大WAP网站的搜索引擎、电子邮箱，查询股票行情、新闻、天气等等。 <br>\r\n<br>\r\n此外，N188也具有多重声控功能，例如20组声控指令、20组声控拨号以及20组语音记事，你甚至可以声控上网。另外，N188也具有日历、日程、计算器等基本的个人事务功能。 <br>\r\n<br>\r\n三星的游戏一直是手机种最多的，N188同样提供了多达11个内置游戏，比如贪吃蛇、吃角子老虎、堆方块等等。可以帮你消磨无聊时光哦！", "0", "0", "2002-12-06 00:00:00", "pic/n188.jpg", "不详", "1050", "1600", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "True");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("138", "25", "1", "三星N288", "1200", "SGH-N288将延续N系列的典型布局，以超薄、超轻为主要卖点。N288重量将只有69克、机身厚度仅有1.49厘米，于是“排骨机”的绰号便不期而至了。", "内建WAP1.1版浏览器 <br>\r\n声控上网、声控操作、声控拨号 <br>\r\n五组来电分组功能 <br>\r\n三组自编铃声、铃声下载 <br>\r\n电话记忆容量100组 <br>\r\n20组声控拨号 <br>\r\n蓝色冷光液晶显示萤幕 <br>\r\nGSM900/1800 <br>\r\n尺寸：100X40X14.9mm <br>\r\n重量：69g <br>\r\n待机：60小时(纤薄电池)；90小时(标准电池) <br>\r\n通话：2小时(纤薄电池)；3小时(标准电池) <br>\r\n颜色：香槟金、炭黑 <br>\r\n配置：二电一充<br>\r\n<br>\r\n<br>\r\nSGH-N288将延续N系列的典型布局，以超薄、超轻为主要卖点。N288重量将只有69克、机身厚度仅有1.49厘米，于是“排骨机”的绰号便不期而至了，呵呵…… <br>\r\n<br>\r\nN288线条简洁、体型干净利落，香槟金的外壳及黑色炔棵姘澹更突显出高雅的质感及王者尊贵的气度，科技味十足。。超薄机内建5种生动有趣的屏幕保护动画，有蝴蝶、花豹、小狗、农村与花朵等，使手机的待机屏幕一点也不乏味。同时这款手机配备了三星高分辨率液晶屏幕(128 X 64 象素，其它内建功能还包括 100 组电话簿、T9中英文智能输入，以及 3 组自编铃声、5 组来电铃声分组、30首可选铃声。颜色方面，除了香槟金外，另有黑色让消费者选择。 <br>\r\n<br>\r\n排骨虽轻、虽薄但千万不可认为是瘦弱啊！相反，N288具备强悍的技能SGH-N288 超薄机主要锁定高阶商务人士，此款手机可说是内外兼具。功能方面除了可储存 20 组声控播号、20组声控指令、无线及声控上网外，繁忙的商务人士更　要个人资讯管理(PIM)，建立行事历并可设定闹铃提醒约会。而内建答录机功能，在开会或不方便接听电话时，将发挥最大的效用，以及内建11组游戏，在闲暇之馀，可娱乐放松一下。外型线条简单、设计俐落，轻巧纤薄的体积，香槟金的外壳及黑色内部面板，更突显出整款手机高雅的质感及王者尊贵的气度，科技感中带有强烈的个人风味。此款功能完备，外型高质感的手机，预计将提供追求精品级手机的消费者另一个选择。", "0", "0", "2002-12-06 00:00:00", "pic/n288.jpg", "不详", "1200", "2050", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("140", "25", "1", "三星N628", "1600", "和弦手机N628，采用了16和弦的铃声系统，音乐非常地动听。", "网络类型：GSM900/1800MHz \r\n重量：83 克 \r\n尺寸/体积：100×43×19.5mm \r\n通话时间：140分钟 * \r\n待机时间：90小时 * \r\n\r\n【主要功能】 \r\n\r\n自动双频 \r\n屏幕显示 屏幕：128×64 像素 \r\n蓝色屏幕 \r\n显示屏显示汉字2行 \r\n特别细致全荧幕动画图像显示 \r\n图像信息跨网互传 \r\n六种信号灯指示 \r\nWAP浏览器 \r\n中文输入[T9智能中英文输入法] \r\n繁简体/拼音笔划输入 (支援电话薄、短信、及用户输入铃声名称) \r\n中文短信息收发 \r\n短讯信息发送报告 \r\n短讯图片功能 \r\n日期时钟 \r\n内置振动 \r\n先震后响 \r\n语音拨号 \r\n声控指令 \r\n话音备忘 \r\n电话答录机 \r\n可选铃声[30种] \r\n自编铃声[2种] \r\n下载铃声[2种] \r\n和弦铃声[16和弦] \r\n通话时间提示 \r\n话机电话簿可存储[500个]电话 \r\n分组铃声，辨别不同来电 \r\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 \r\n动画屏保 \r\n内置游戏[3个]:堆积目、射击、推物体 \r\nEFR、STK服务 \r\n多种闹铃(仅闹一次，日?钟，周闹钟) \r\n日程表 \r\n日历 \r\n计算器 \r\n世界时钟 \r\n简易国际电话拨号 \r\n支持传真业务 \r\n支持电子邮件 \r\n自动锁键盘 \r\n\r\n配置：一锂电、一直充\r\n\r\n\r\n这台N628采用了16和弦的铃声系统，音乐非常地动听！ \r\n\r\n其它功能方面：N628的显示屏继承了R208的蓝色屏幕，在屏幕上还可以全动画图像显示。三星在N628里增加了图片互发和短信息发送报告这两样具有某品牌特色的功能，也算是取别人之长了。语音拨号和声控指令这两样三星手机的强项当然有所保留。N628内置了500个话机电话薄，但游戏就比之前的三星机少了不少，只有堆积目、射击、推物体这三个游戏。由于N628是一台没翻盖的手机所以还增加了一项自动键盘锁功能，这功能可以在用家设定的某一时间里而用家又没操作的时候自动锁住键盘以免误按。", "0", "1", "2002-12-06 00:00:00", "pic/n628.gif", "不详", "1580", "2000", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("141", "26", "1", "阿尔卡特OT525", "1380", "新款上市**阿尔卡特ＯＴ５２５", "网络类型：GSM 900/1800MHz/GPRS <br>\r\n重量：75 克 <br>\r\n尺寸/体积：98×42×21mm/72cc <br>\r\n通话时间：120-180分钟 <br>\r\n待机时间：90-150小时 <br>\r\n配置：二电一充<br>\r\n<br>\r\n<br>\r\n可更换外壳 <br>\r\n屏幕可反白 <br>\r\n全中文输入 <br>\r\n中文短信息收发 <br>\r\nEMS短信 <br>\r\n分类一次性删除短消息 <br>\r\n内置振动 <br>\r\n录音[180秒] <br>\r\n录制铃声 <br>\r\n语音拨号[50个] <br>\r\n可选铃声[30种] <br>\r\n和弦铃声 <br>\r\n自编铃声[2首]，每首40音符 <br>\r\n无线下载功能[下载铃声、图片] <br>\r\n通话时间提示 <br>\r\n话机电话簿可存储[250个]电话 <br>\r\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\r\n免提通话 <br>\r\n内置游戏[3个]；可通过SMS传送分数 <br>\r\n可直接用手机将信息发至对方电子邮箱", "0", "0", "2002-12-06 00:00:00", "pic/ot525.jpg", "不详", "1350", "2300", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "True");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("142", "26", "11", "阿尔卡特OT715", "1850", "阿尔卡特发表首款高端GPRS移动电话―OT715。OT715的设计将商务及休闲的功能融合为一，让商务人士发挥专业能力的同时，还能保持轻松的休闲心情。", "【网络类型】 GSM900/1800MHz <br>\r\n【尺寸体积】 116×43×20mm <br>\r\n【手机重量】 88克 <br>\r\n【通话时间】 最多390分钟 <br>\r\n【待机时间】 最多250小时 <br>\r\n【上市时间】 2002年08月 <br>\r\n<br>\r\n配置：全新全套！一电一充、数据线、光盘！<br>\r\n<br>\r\n<br>\r\n阿尔卡特发表首款高端GPRS移动电话―OT715。OT715的设计将商务及休闲的功能融合为一，让商务人士发挥专业能力的同时，还能保持轻松的休闲心情。 <br>\r\n<br>\r\nOT715的外型设计具有70年代的复古风格，更引以为傲的是OT715拥有加大显示屏幕，但体积一样轻薄短小，且超高分辨率的屏幕显示(100x150 图素，4灰阶蓝色背光)，让移动商务更为便利。 <br>\r\n　　 <br>\r\n利用内建屏幕功能选向，OT715的超大屏幕可以显示多达10行文字，而且有局部放大的显示功能。此外，OT715还可用动态方式下载更复杂的图案、照片、全屏幕动画图案、与更先进EMS 4.0版的铃声旋律等等。阿尔卡特还为对音乐特别讲究的使用者，提供多达16种乐器编曲的和弦铃声。OT715融入所有先进服务，光是WAP就以提升到 1.2.1版。 <br>\r\n　　 <br>\r\n超大的屏幕设计更强化OT715的游戏功能。OT715的使用者可以相互比较游戏得分，次外还可网内对打，看看谁的功力高强。 <br>\r\n　　 <br>\r\nOT715内建更强大的秘书功能：通讯簿可储存多达800组电话号码，还可纪录1000个约会，以及60笔待办事项的提醒，让最繁忙的商务人士，都能更发挥的更有效率。OT715还可连接PC以无线传输资料，且内建计算器、欧元货币换算及世界时间转换功能。除了以上实用功能超强，OT715还让移动通讯增添趣味：使用者可以将通讯簿里联络人的照片储存在手机内，当联络人来电时，手机屏幕就显示来电者的相片，让接听电话增添更多趣味。 <br>\r\n　　 <br>\r\nOT715如同最佳的贴身秘书，能够精确记忆使用者的声音，其语音备忘录可储存多达20组、每组60秒的备忘事项；而OT715还可建立20组的语音拨号，让使用者可以更轻松操作。 <br>\r\n　　 <br>\r\n为了让使用者的操作更为便利、有效率，OT715内建红外线传输、电子名片及电子行事历，且适用于PALM与Windows CE。", "0", "0", "2002-12-06 00:00:00", "pic/ot715.jpg", "不详", "1800", "2900", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
