INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("147", "25", "4", "三星Anycall-A188", "1250", "三星AnycallSGH－A188", "网络类型：GSM900MHz/DCS1800MHz <br>\n重量：89 克 <br>\n尺寸/体积：80×42×22.9mm <br>\n通话时间：120-200分钟 * <br>\n待机时间：40-70小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 <br>\n中文输入[拼音、笔划等7种键盘输入方式] <br>\n中文短信息收发 <br>\n日期时钟 <br>\n内置振动：可快捷键设置 <br>\n可选铃声[19种] <br>\n自编铃声[2种]：利用图形乐谱 <br>\n电话簿分组铃声和图片识别 <br>\n通话时间提示 <br>\n话机电话簿可存储[99条]电话 <br>\n通话记录[10个已接、10个未接、10个已拨电话号码并带时间] <br>\n图形菜单 <br>\n内置游戏[7种] <br>\n红外线接口 <br>\nEFR、STK服务 <br>\n闹钟 <br>\n日历 <br>\n日程安排 <br>\n记事本 <br>\n世界时钟 <br>\n折叠式功能翻盖 <br>\n快速拨号[9个] <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n作为注重科技与品质的三星电子的产品，三星AnycallSGH－A188移动电话在继承了所有三星电子优良传统的同时，更以其简洁的线条和尽显时代感的造型设计荣膺欧洲IF，即汉诺威工业设计大奖，这必定会影响今后手机市场的流行趋势。SGH…A188是一款集典雅、时尚、高科技于一体的手机精品，相信一定会让与它同样出色的各行业精英和成功人士爱不释手。亮丽的珍珠白高贵典雅，另有香槟金、银白色，令杰出的你更富时尚魅力。 <br>\n<br>\nA188特别强调外型轻巧高贵、同时主体结构坚固耐用。机身采用符合人体工学的翻盖式设计，为了避免话盖松脱，三星特别设计较坚固的翻盖轴心，并选用目前国际上最先进的技术和最新的材料制作而成，硬朗结实、坚固耐用，厂家自信地作出保证5万次开翻盖的承诺，让消费者对产品充满信心，并自由地展现其开朗的性格。同时A188在电池与机体接合处也特别装置双锁扣，可以防止手机不小心摔到时发生电池分离的情况。 <br>\n<br>\n三星电子集团认为“卓越、成功、高雅、珍爱、个性”既是SGH―A188这一款手机的品质，也是喜爱使用SGH―A188的人所拥有的品质。", "0", "1", "2002-12-07 00:00:00", "pic/a188.gif", "不详", "1250", "1980", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
