INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("169", "31", "4", "诺基亚5510", "1080", "全新诺基亚5510手机资集音乐播放、调频收音机和游戏机于一身，成为新一代娱乐型手机。", "网络类型：GSM900/1800MHZ及EGSM <br>\n重量：155 克 <br>\n尺寸/体积：134×58×28mm <br>\n通话时间：150-270分钟 * <br>\n待机时间：55-260小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 、QWERTY型双手键盘 <br>\nNavi TM快捷键 <br>\n多任务操作 <br>\n日历标签：220个 <br>\n64MB内存 可作即插即用移动硬盘 <br>\n可存储2小时的CD音质的音乐 <br>\n中文输入[拼音与笔划输入法] <br>\n中文短信息收发 <br>\n聊天室功能 <br>\n群组短信 <br>\n支持发送和接收长信息，最长可达201个字符 <br>\n日期时钟 <br>\n内置振动 <br>\n动画屏保 <br>\n话机电话簿可存储[100个]电话 <br>\n内置数码music player <br>\n内置游戏[5个]:贪吃蛇2、弹珠台、空间大战、记忆力游戏2、捡金豆 <br>\n通过诺基亚俱乐部可下载更高水平的游戏 <br>\n可选铃声[35种] <br>\n下载铃声[7种] <br>\n无线下载功能[下载铃声、图片、游戏等] <br>\n快速拨号，可设置9个名字（按键1通常用于语音拨号） <br>\n语音拨号[8个] <br>\n可直接重拨最后一个电话号码 <br>\n自动重拨（最多10个） <br>\n自动应答 <br>\n情景模式 <br>\n紧急呼叫112不使用SIM卡、按键锁定 <br>\n呼叫等待、呼叫限制、呼叫转移、呼叫计时 <br>\n自动手动网络选择 <br>\n固定拨号号码、仅用于预置号码 <br>\n数码音乐播放器：可播放AAC和MP3格式文件 <br>\n数码音乐录制器：可从调频立体声广播录制，也可通过电缆连接录制外部音乐 <br>\n调频立体声广播 <br>\n数码音乐播放：长达10小时 <br>\n调频立体声广播：长达13.3小时 <br>\n内置立体声调频收音机可以保存10个调频广播频道 <br>\n使用诺基亚音频管理2.1PC软件下载和管理数码音乐文件 <br>\nWAP无线上网 <br>\n动画屏保 <br>\nEFR、STK服务 <br>\n时钟和闹铃 <br>\n闹钟(支持关机闹铃) <br>\n计算器 <br>\n模式转换 <br>\n秒表 <br>\n倒计时功能 <br>\n语言种类 简体中文、繁体中文、English、自动 <br>\n配置： 一块锂电池，充电器，数据线，ADA－2适配线，ADE－2音频线、立体声耳机+话筒线、光盘. <br>\n<br>\n<br>\n<br>\nNOKIA 5510 以其独创的外形设计，整合许多时下年轻人最喜爱的功能，包括：数字音乐随身听，可用来播放 MP3 及 AAC 格式的音乐文件、数字化音乐录音功能、具立体音响的 FM 收音机、各式简讯服务、移动游戏以及 WAP 浏览器于一机。使用者可以利用销售包装中附的 NOKIA 音乐管理软件(NOKIA Audio Manager)和数据传输线，将自己喜欢的 CD 音乐下载到 NOKIA 5510 中。使用者不仅在移动时可以享受音乐随身听或使用 FM 收音机，即使在玩游戏时也可同时享受喜爱的音乐。 <br>\n<br>\n具有完整 QWERTY 式键盘(类似计算机全按键式键盘)的 Nokia 5510 不但是设计独特的娱乐性手机，也是一具提供多方聊天室(需运营商支持)、多方短信传送、加长型短信、通过 WAP 下载游戏模块及动画屏幕保护程序等功能的装置。特殊的键盘设计使输入文字及撰写 email 变得更容易。其它创新的功能还包括以 NOKIA 音乐管理软件建立音乐图书馆和播放清单、收音机频道自动搜寻、及最多储存 10 个预设电台等功能。使用者还可在 Nokia 5510 的 64MB 内存中储存长达两小时的音乐。", "0", "0", "2002-12-07 00:00:00", "pic/5510.jpg", "不详", "1060", "1500", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("170", "31", "4", "诺基亚9210", "4500", "商务手机功能最强大９２１０", "网络类型：GSM900/1800MHz <br>\n重量：244 克 <br>\n尺寸/体积：158×56×27mm <br>\n通话时间：240-600分钟 * <br>\n待机时间：80-230小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n功能齐备的“移动办公室” <br>\n这一移动办公室中包括了自动双频移动电话、传真、电子邮件、互联网、WAP、文字编辑、电子表格、演示文稿、日历和通讯录 <br>\n可以创建并编辑文档文件和电子表格 <br>\n以彩色显示屏查看并演示文稿幻灯片 <br>\n可以收发具有附件的电子邮件 <br>\n与PC相似，具有色彩丰富并配有图标的友好的用户界面 <br>\n翻盖[折叠式] <br>\n内置天线 <br>\n中文输入 <br>\n中文短信息收发 <br>\n内置振动 <br>\n可选铃声，可自编铃声 <br>\n计算器、日期时钟以及多种游戏 （可下载） <br>\n高速浏览互联网（HSCSD网络） <br>\n支持高速电路交换数据（HSCSD）的网络服务 <br>\n基于GSM，以最高速率达每秒43.2Kb的数据传输速率访问互联网和WAP服务 <br>\n以每秒可达28.8Kb的速率收发电子邮件 <br>\n亮丽的彩色屏幕和多媒体功能 <br>\n支持4096色的高品质彩色显示屏 <br>\n下载并以彩色方式浏览图像，还可将其保存为墙纸 <br>\n可与兼容的数字照相机相连 <br>\n可下载并播放视频剪辑 <br>\n无线下载功能[下载铃声、图片、游戏等] <br>\n话机电话簿可存储电话 <br>\n可以录制铃声：录制并播放声音文件 <br>\n技术参数 <br>\n基于RISC的ARM-9为32位CPU，频率为52MHz，内建8M的SDRAM，16M的用户空间 <br>\n操作系统：Symbian EPOC R6的Crystal，32位的操作系统 <br>\nMMC存储卡插槽 <br>\n连接方式：IrDA, Ir-TranP, RS-232 <br>\n低功耗 <br>\n640x200@4096色的有源阵列彩色TFT大液晶屏幕（11cmx3.6cm的尺寸）,完整的英文键盘。 <br>\n日历和通讯录的兼容性 <br>\nMicrosoft Outlook 98/2000 <br>\nSyncML remote synchronisation <br>\nSyncML 远程同步工具 <br>\n光盘（销售包装）中提供的丰富内容 <br>\n用于PC的附加软件 <br>\n用于9210c个人通讯器的附加软件 <br>\n用户指南 <br>\n附加应用程序指南 <br>\nPC套件指南 <br>\n光盘中的软件指南 <br>\n附加的图像、声音文件和视频剪辑 <br>\n轻松入门 自学光盘 <br>\nPC 套件兼容性 <br>\n- MS Windows 98/2000/NT4.0. <br>\n- 文件转换: <br>\n-- MS Word97, MS Word 2000Symbian OS <br>\n-- MS Excel 97, MS Excel 2000Symbian OS <br>\n- 日历和名片夹同步: MS Outlook 97/98/2000. <br>\n- 电子邮件同步: MS Outlook 97/98/2000 <br>\nWindows文件兼容性（不需要第三方软件支持的情况下对MS Office文档保持兼容）: <br>\n-- MS Office 97/2000 <br>\n-- MS Word 97/2000, <br>\n-- MS Excel 97/2000, <br>\n-- MS Powerpoint 97/2000 <br>\n电子邮件支持 SMTP, POP3 and IMAP4 <br>\n通过测试的电子邮件服务器: <br>\n- Stalker : 1.5.2 <br>\n- MS Exchange : 5.5 <br>\n- Lotus Domino : Chinese 5.0 <br>\n<br>\n配置：全套齐全<br>\n<br>\n<br>\n<br>\n诺基亚9210是为了迎接移动互联网时代而设计，它集无线电话和多媒体掌上电脑于一体，综合了传真、电子邮件、日历、图像、无线应用协议(WAP)、互联网(www)等功能。诺基亚9210支持大部分PC机上的办公软件，可以创建Word和Excel文档，还可以观看PowerPoint幻灯片。 <br>\n<br>\n诺基亚9210重244克，具有支持4096种颜色的高清晰显示屏，配备标准锂电的9210通话时间可高达10个小时，待机时间为230小时。9210还支持HSCSD(High Speed Circuit Switched Data，即高速电路交换数据)，HSCSD可支持达43.2kbps的高传速网络服务。", "0", "0", "2002-12-07 00:00:00", "pic/9210.gif", "不详", "4400", "6200", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("171", "31", "4", "诺基亚8850“高贵”", "1999", "这机我想不用多介绍,大家都很清楚了!", "网络类型：GSM900MHz/DCS1800MHz <br>\n重量：91 克 <br>\n尺寸/体积：100×44×17mm <br>\n通话时间：120-200分钟 * <br>\n待机时间：50-150小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 <br>\n内置天线 <br>\n支持翻盖功能（滑盖） <br>\n中文输入[拼音、笔划或注音键盘输入方式] <br>\n英文电话簿 <br>\n中文短信息收发：可发送或接收图片信息 <br>\n日期时钟 <br>\n内置振动 <br>\n语音拨号[8个] <br>\n可选铃声[35种] <br>\n自编铃声[5种]：通过下载或红外传输方式 <br>\n情景模式 <br>\n话机电话簿可存储[250条]电话：并可存储50项日历记事 <br>\n通话记录[10个已接、10个未接、10个已拨电话号码] <br>\n内置游戏[4种]：贪食蛇、记忆力、逻辑猜图和旋转排序（两个兼容的诺基亚手机间可实现交互式游戏） <br>\n红外线接口 <br>\nEFR、STK服务 <br>\n闹钟 <br>\n日历 <br>\n计算器 <br>\n备忘录：配有提示器 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\n8850的外观的确配的上&quot;高贵&quot;两个字，让人一眼就可以迷上了，比起8810，昔日的锐气虽已不再那么剌眼，但其内敛的光芒却更慑人心魂;她的外壳盖改采镁合金与铝合金一起制成， 并一改 8810 钛银电镀的材质而使用雾面烤漆， 让 8850 不再像 8810 那样很容易刮伤与附着指纹，漂亮却不粘手，其钛金属色铝合金，配合电镀铬键盘及外框，让人拿在手上爱不释手。她的冷光绝不炫丽，但白中但着浅蓝的光采就好似情人含情脉脉的眼神，悠悠的看着你，也足够让你目炫神迷的了。轻拉下滑盖之后，更是另人忍不住赞叹:她的按键虽然有点太小，但其质感却更添她的风采。其中一样也是很用心的设计是她不但把天线隐藏起来，连背盖的开关也是很巧妙的设计在边框上，不注意还找不到呢!必需将边框上的按钮按下去，再将背盖拉下。 <br>\n<br>\n如果你看过她的内在美，保证你的口水一定会滴下来。它的背后架构跟8210差不多.也是要先将SIM卡装进去再放上电池的顺序，电池也是搭配了650mah锂电，但内装可就差多了，全部是金属材质，绝对不是&quot;金玉其外、败絮其中&quot;的那一型。我相信，在现在一片机海当中。它绝对是能让你眼睛一亮的机种，在外型上，当之无愧的有五颗星的评价。 <br>\n<br>\nNokia 8850承习着Nokia一贯的操作方式及功能，使用过6150或3210的人在很短的时间内就可以上手，如果是8210的使用者的话就更快了(因为几乎一模一样，再加个8组声控拨号)，下面是它的功能特色: <br>\n<br>\n中文双频、有中文输入功能， 但只能用于简讯功能，电话簿则不行。大家一定很纳闷，有中输的功能为什么不能应用在电话簿呢??我也很纳闷，在Nokia的回答是，在手机上输入中文多麻烦啊!!我们有提供红外线的功能啊!!直接在计算机上传过去就可以了，原来他们的设计原理是这样子，但是我们有这样的习惯及设备的人毕竟还是占少数，所以还是希望他们提供升级的服务。 <br>\n<br>\n菜单选单还是可爱的动画模式，而在每个细项亦有类似Ericsson T28sc的说明功能，只要在其功能上停止几秒，就会出现说明窗口，为不爱读书的人造福，蛮方便的。 <br>\n<br>\n中文输入功能:在简讯方面8850也和8210一样多加了中文输入的功能，使用者可以在简讯中直接输入中文，当然，你也可用&quot;#&quot;字键来切换中英文输入，不过在传送图片功能方面，就只能输入英文，这是个美中不足的地方。另外在电话簿功能，则不能使用中文输入功能，不过因为有红外线功能，所以可以以红外线传送接收及打印其内容。 <br>\n<br>\n声控拨号功能:8850也加入了声控功能的行列了，先恭喜他了，8850有8组空间可记录语音标志。不过也许刚加入这个技术，在辨视率及操作方式均不及Philip、Ericsson、Motorola等其它厂牌便利，要经过输入号码后再出来录制两个步骤才能完成，要使用声控功能时只要按住电话簿的功能键数秒就会出现提示，虽然辨视率不是很高，但8850有提供一项贴心的小功能，就是可以让你再去听听之前录的声音，让你即使忘了当初录了什么内容也不必担心了。 <br>\n<br>\n8850的收讯相当不错，更进步的是它的收讯也更稳定了，克服了隐藏式天线的缺点，之前3210及8210的收讯常常飘浮不定，由其在手指靠近天线的时候，但8850的格数不但很漂亮，几乎常常在满格的位置，也相当稳定，我想应该恭喜及赞赏Nokia，在技术上又是一大突破了。", "0", "1", "2002-12-07 00:00:00", "pic/8850_2.gif", "不详", "1970", "2450", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("172", "31", "4", "诺基亚8855", "2400", "诺基亚8855.诺基亚为了丰富其高贵尊崇型移动电话系列。", "网络类型：GSM900/1800MHz <br>\n重量：98 克 <br>\n尺寸/体积：102×46×21mm <br>\n通话时间：255分钟 * <br>\n待机时间：225小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 <br>\n翻盖[滑盖式] <br>\n碳晶色亚光金属质感表面 <br>\n简明纯粹的线条、赏心悦目的曲线 <br>\n符合人体工程学的按键设计 <br>\n内置天线和振动提示功能 <br>\nWAP^(无线应用协议) <br>\n可在移动中接入移动互联网服务，如新闻、天气预报、航班查询和手机银行等 <br>\n中文输入[拼音、笔划或注音键盘输入方式] <br>\n中文短信息收发 <br>\n日期时钟 <br>\n内置振动 <br>\n语音拨号 <br>\n可选铃声 <br>\n自编铃声 <br>\n情景模式:4种标准的诺基亚情景模式设置及2种可下载的情景模式 <br>\n可选择在一段时间内保持某一特定的情景模式 <br>\n通话时间提示 <br>\n话机电话簿可存储[250个]电话 <br>\n通话记录[10个]已接、[10个]未接、[20个]已拨电话 <br>\n动画屏保 <br>\n全屏显示屏幕保护图案 <br>\n屏幕保护图案 <br>\n指针式时钟显示 <br>\n独特的液晶显示 <br>\n白色亮光屏幕提供更清晰的显示 <br>\n世界时钟 <br>\n图形菜单 <br>\n内置游戏[4个]：贪食蛇二，空间大战、弹珠台游戏、捡金豆 <br>\n红外线接口 <br>\n内置Modem <br>\nEFR、STK服务 <br>\n闹钟 <br>\n100条日历备忘录 <br>\n可重复的日历备忘录功能可用于定期会议或备忘录的提示 <br>\n两星期的日程表显示 <br>\n计算器 <br>\n<br>\n配置；一锂电、一直充<br>\n<br>\n<br>\n<br>\nNOKIA 8855是专为懂得欣赏生命中精致事物的品味人士所设计，以简单而雅致的风格呈现极简主义的概念 <br>\n<br>\n诺基亚行动电话事业部亚太区策略行销副总裁骆士东（Nigel Rundstrom）表示：“在创造极致品质的前提下，NOKIA 8855结合了雅致而简单的设计与色彩，同时也具备各种实用的功能，相信将会获得那些注重设计、同时也讲究内在的品味人士的欣赏”。 <br>\n<br>\nNOKIA 8855延续尊容品味系列独特的滑盖设计，呈现极简而高雅的风格。其中，滑盖和大部分零组件都是以精致铝镁合金制成，并呈现优雅的石墨色泽，而按键部份和屏幕外框则采镀铬材质。 <br>\n<br>\nNOKIA 8855是GSM 900/1800双频行动电话，并具有WAP功能，使用者可以在行动中轻松获得行动服务及信息娱乐讯息。这款高雅的行动电话还具有多种贴心的功能，包括领导流行的动画指针表、可同时浏览两周的日历、及「重复日历备忘录」选项、「先振动后响铃」铃声选项、及语音拨号等。此外，NOKIA 8855支持中文电话簿，还提供多方简讯传送、简讯聊天室、加长型简讯等简讯功能。NOKIA 8855以内建电池维持整体外型的流畅设计，标准的BLB-3 1000毫安锂电池连续通话时间最长可达4小时15分，待机时间最高可达225小时。", "0", "0", "2002-12-07 00:00:00", "pic/8855.gif", "不详", "2350", "3200", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("173", "31", "4", "诺基亚6510", "1900", "诺基亚最新的商务机型,湖蓝色背景屏幕&nbsp;,支持GPRS,更带有FM调频收音机&nbsp;!", "网络类型：GSM900/1800MHz/GPRS <br>\n重量：84 克 <br>\n尺寸/体积：97×43×19mm/67cc <br>\n通话时间：210分钟 * <br>\n待机时间：288小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频(EGSM900/GSM1800MHz) <br>\n内置天线 <br>\n湖蓝色背景屏幕 <br>\n声控命令，语音备忘 <br>\n大容量动态内存+ <br>\nFM调频收音机 <br>\n小巧轻便 <br>\n内置天线 <br>\n全新的宜人的湖蓝色背景屏幕 <br>\n高清晰度显示屏分辨率为96*64可显示4行文字+1行菜单 <br>\n可更换外壳[前后壳]五款颜色或与8310外壳互换 <br>\n精心设计的人体工程学键盘 <br>\n专业、商务和个人应用之间实现完美平衡 <br>\nGPRS#移动电话 <br>\nGPRS可带给你“总是在线”的全新体验，节省时间且方便 <br>\n支持快速、灵活的互联网/WAP浏览和电子邮件功能 <br>\n移动互联网服务(WAP) <br>\n支持WAP 1.2.1浏览器，让您获得更出色的浏览体验 <br>\nWAP浏览器中提供的更为方便的文本输入方式，使得数据输入更加轻松 <br>\n提供“总是在线”连接，让您可以通过基于GPRS的WAP功能访问包含丰富数据和服务的精彩世界 <br>\n电子钱包# <br>\n支持vCard[可将通讯资料如: WAP网站上的名片 存到手机上] <br>\n支援vCalendar [可⑼路上的个人记事本如: WAP网站上的名片存到手机上] <br>\n提供方便易用的WAP#在线交易 <br>\n可更换外壳[前后壳]六款颜色或与8310外壳互换 <br>\n在电话中存储并加密您的信用卡信息，通过个人PIN代码对其进行保护 <br>\nWIM(无线识别模块)用于存储并处理个人信息，同时保护交易的完整性 <br>\nWTLS(无线传输层安全)用于服务器身份验证和数据加密，以便提供更高级的会话安全性(支持WTLS级别1和级别2) <br>\n中文输入[T9拼音、T9笔划、T9英文联想或注音键盘输入方式] <br>\n中文短信息收发[机身可存150条文字短信息或50条图文信息] <br>\n日期时钟 <br>\n内置振动 <br>\n录音[180秒] <br>\n可单独录制声音片段，亦支持通话中双向录音。可分段录制，总长度180s <br>\n语音拨号[25个] <br>\n增强语音功能可声控指令启动收音机、情景模式、语音信息、以及启动红外线功能 <br>\n支持语音信箱、留言接听功能 <br>\n声控命令 [5个] <br>\n快速拨号，可设置9个名字（按键1通常用于语音信箱） <br>\n可选铃声[35种] <br>\n无线下载功能[下载铃声、图片、游戏] <br>\n手机内保存的所有振铃声都可以设为短消息提示音 <br>\n单键拨号、一指静音功能 <br>\n下载铃声[5种](升级软件版本后可增至十首) <br>\n情景模式 [5种]](标准、无声、会议、户外和寻呼机) <br>\n通话时间提示 <br>\n话机电话簿可存储[500个]电话 <br>\n手机与SIM卡电话簿互相拷贝 <br>\n每个联系人项下3个号码和1个文字项(每个联系人名下最多可存储5个号码和4个文字项);150条文字短信息或50条 图片信息；100至250条日历备忘录;30条待办事项 <br>\n通话记录[10个已接、10个未接、20个已拨电话]]，带日期时间 <br>\n通话后可显示通话时间 <br>\n可显示帮助信息 <br>\n红外线接口：高速电路交换数据(HSCSD)# <br>\n可通过红外接口连接到兼容的笔记本电脑的内置HSCSD调制解调器 <br>\n内置游戏 [4种]：贪食蛇2、记忆力2、空间大战、弹珠台 <br>\n可通过WAP或GPRS到诺基亚网站下载更多好玩刺激的游戏 <br>\n诺基亚下载城提供了贪食蛇2新迷宫、空间大战新场景、弹珠台新球台(游戏服务) <br>\nGPRS计时器(上网时间)、计数器(数据流量) <br>\n连续数据传输、文件下载和电子邮件的理想选择 <br>\n支持数据传输速率最高可达43.2 kbps <br>\nEFR、STK服务 <br>\n闹钟[支持关机闹钟] <br>\n增强型的日历功能可显示年、月、日、星期和周期 <br>\n通过增强的日历功能，轻松地安排生活 <br>\n周日历显示功能令你更清楚了解约会安排 <br>\n您可以通过SMS#或红外接口来发送和接收日历备忘录 <br>\n随时跟踪最新的事件和报道 <br>\n让您可以存储多达20个预置频道 <br>\n两用耳机免提套件 <br>\n方便的语音功能 <br>\n计算器 <br>\n自动键盘锁 <br>\n秒表 <br>\n自动锁键盘功能(10秒-1小时) <br>\n选择日期快捷方便 <br>\n主要功能键、电源键、音量调节键、后盖开启键 <br>\n该机可以同兼容手机进行联机贪食蛇游戏 <br>\n最新软件版本：V4。12 <br>\n免提短信报告，区别一般短信 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\n精巧而功能丰富的NOKIA 6510巧妙地结合了商务及个人使用的功能，包括新的淡蓝色LED背景光、电子钱包应用、GPRS、计算机手机编辑软件的应用及内建FM收音机。是一款支持诺基亚 Xpress-on? 前后换壳的 GPRS 行动电话。 <br>\n<br>\n6510外型一反 8310 绚丽的设计而回归传统线条，搭配上全图形操作界面的高分辨率屏幕搭配新的淡蓝色 LED 背景光，强化了屏幕的清晰度。宽大的屏幕让使用者可以一次浏览四行完整的英文、或是三行完整的中文内容，特别适合浏览行动网际网络、阅读简讯、电话簿与加强的日历备忘录等功能。并增加丰富的个人化功能、以及内建调频 FM 收音机。相信 6510 将吸引起那些希望拥有一款外型平实不凡、内在多功能的行动电话的商务时尚人士。 <br>\n<br>\nNOKIA 6510的最重要的功能就是最新的电子钱包应用，使用者可透过WAP浏览器进行方便又安全的线上交易，并利用移动电话中的安全模块完成交易的数字签章。透过WAP使用信用卡付款，电子钱包也让线上购物变得非常简单。此外，使用者还可以利用电子钱包功能储存其姓名、密码、和号码等个人资料。NOKIA 6510同时也支持电子商务模型语言标准 (ECML 即Electronic Commerce Modeling Language,是一套运用于数字钱包及线上商务的开放式网际网络标准，让交易的资料得以自动交换。) <br>\n<br>\nNOKIA 6510的高分辨率屏幕搭配新的淡蓝色LED背景光，强化了屏幕的清晰度。宽大的屏幕让使用者可以一次浏览四行完整的英文、或是三行完整的中文内容，特别适合浏览移动网际网络、阅读简讯、电话簿与加强的日历备忘录等功能。此外也支持WAP 1.2.1版、GPRS、及HSCSD (高速电路交换数据)，有助于弹性的无线数据联机。NOKIA 6510可录制长达3分钟的语音录音，并具有10个语音卷标的声控拨号功能、及最多可达五个指令的语音指令功能。6510 内建 500 条联络人的电话簿：基本上，每个均可建立三组电话号码与一组介绍说明；不过您也可以每人建立多达五组电话与四组文字介绍，只是这样会因为占据较多的内存而减少电话簿的总人数。6510 的内置内存还可以记录多达 150 组文字、50 组图形简讯、或最多 500 组的行程。", "0", "0", "2002-12-07 00:00:00", "pic/6510.jpg", "不详", "1850", "2550", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("174", "31", "4", "诺基亚8310", "1880", "诺基亚8310作为首款面世的诺基亚GPRS手机，是专为热爱生活、喜欢高科技产品的前卫人士所设计。诺基亚8310有三种不同颜色的彩壳可供选择，开机时，诺基亚8310闪烁起白色背景光，中央按键也随之而亮。", "网络类型：GSM/GPRS <br>\r\n重量：84 克 <br>\r\n尺寸/体积：98×43×17mm/66cc <br>\r\n通话时间：150-240分钟 * <br>\r\n待机时间：100-400小时 * <br>\r\n<br>\r\n【主要功能】 <br>\r\n<br>\r\n自动双频(EGSM900/GSM1800MHz) <br>\r\n支持GPRS <br>\r\n内置天线 <br>\r\n主要功能键、电源键、音量调节键、后盖开启键和屏幕边框采用镀铬设计 <br>\r\n采用冷白色背景光(LED光源)以及键盘下透光设计 <br>\r\n键盘灯有6盏，可以通过透明外壳映射出四盏出来 <br>\r\n可更换外壳[前后壳] <br>\r\n可更换外壳[前后壳]可与6510彩壳互换 <br>\r\n九种可自行更换的彩色外壳(幻影黑、激光红、银河白、雪蓝、火红、星灰、黛粉、热金、霜橙) <br>\r\n支持vCard(可将通讯资料如: WAP网站上的名片 存到手机上) <br>\r\n支援vCalendar (可将网页上的个人服务如: WAP网站上的日历备忘存到手机上) <br>\r\n内置WAP 1.2.1 <br>\r\n冉WTAI Public Library (使用者可从WAP网站打电话) <br>\r\n支持发送WAP书签,分组图片 <br>\r\n键盘灯有6盏，可以通过透明外壳映射出来 <br>\r\n中文输入[T9联想式，拼音、笔划或注音键盘输入方式] <br>\r\n中文短信息收发 <br>\r\n大容量中文短信息（可存储150条文字信息或50条图片信息） <br>\r\n短消息提示音可选40种个性铃声（35种内置和5种下载） <br>\r\n支持信息反馈报告 <br>\r\n可以直接从短信息中提取号码 <br>\r\n日期时钟 <br>\r\n内置振动 <br>\r\n单键拨号、一指静音功能 <br>\r\n录音[180秒] <br>\r\n可单独录制声音片段，亦支持通话中双向录音。可分段录制，总长度180s <br>\r\n语音记事可附加闹钟提醒 <br>\r\n语音拨号[10个] <br>\r\n声控命令:可操作情景模式、语音信箱、收音机、红外接口和语音备忘5个相关菜单 <br>\r\n语音信箱（需网络支持） <br>\r\n可选铃声[35种] <br>\r\n下载铃声[5种] <br>\r\n最多可储存40首下载铃声 <br>\r\n情景模式 （内建四种：标准、无声、会议和其他，并可自行定义。） <br>\r\n话机电话簿可存储[500个]电话 <br>\r\n可按拼音首字母查找 <br>\r\n每个联系人名下可最多存储5个电话号码和4个文字项（电子邮件、网址、通讯地址和注释），并支持号码分组及分组铃声、图案 <br>\r\n通话记录[10个已接、10个未接、20个已拨电话号码] <br>\r\n内置游戏[4种]：滑雪游戏、贪食蛇（二）、弹珠台游戏、记忆游戏（二） <br>\r\n该机可以同样版本游戏的手机通过红外联机游戏 <br>\r\n红外线接口:可通过红外线传输通讯录名片 <br>\r\n支持高速线路交换数据（HSCSD） <br>\r\nEFR、STK服务 <br>\r\n支持关机闹钟 <br>\r\n日历:可设置4个备忘录（会议、通话、生日、备忘），前三个可附加非关机闹铃提醒 <br>\r\n备忘录和待办事项 <br>\r\n计算器 <br>\r\n秒表、定时器 <br>\r\n自动重拨 <br>\r\n呼叫等待、呼叫转移、呼叫限制 <br>\r\n内置调频（FM）收音机，随机配备高清晰双声道耳机，无需另行购买 <br>\r\n最新的版本是V5.57 <br>\r\n<br>\r\n配置：一锂电、一直充<br>\r\n<br>\r\n<br>\r\n<br>\r\nNOKIA 8310最大的特色为其独特的美感与个人化的设计，三款基本彩壳采用半透明的材质与多层次质感的设计，营造出三度空间感。NOKIA 8310并采用了最新的色彩、材质、表面处理、与细部设计；其中，可自行更换的双面彩色外壳更将个人化风格发挥至极致，可满足使用者各种不同品味、心情、和场合的需求。", "0", "7", "2002-12-07 00:00:00", "pic/8310.jpg", "不详", "1850", "2550", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "True", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("175", "31", "4", "诺基亚7650", "3880", "全能冠军诺基亚7650,3G时代的象征!本站倾情促销,简体中文最新版仅售3800元&amp;nbsp;!", "网络类型：GPRS，GSM900/1800MHz <br>\r\n重量：154 克 <br>\r\n尺寸/体积：114×56×26mm/138cc <br>\r\n通话时间：240分钟 * <br>\r\n待机时间：150小时 * <br>\r\n<br>\r\n【主要功能】 <br>\r\n<br>\r\n世界上首部2.5G Symbian OS 操作系统的移动电话 <br>\r\nGSM制式, 支持HSCSD高速传输和GPRS三频技术 <br>\r\n高速资料传输：在HSCSD网路每秒43.2　KB；　在GPRS网络上每秒40.2KB　 <br>\r\n高速度数据[43.2　kb/s]　 <br>\r\n操作系统:支持MIDP JavaTM,3 的Symbian5操作系统 <br>\r\n彩色屏幕:采用反射式TFT屏幕[176x208像素，VGA彩色，屏幕大小　35×41mm］ <br>\r\n可显示4,096种颜色　 <br>\r\n图像阵列有640x480 象素 <br>\r\n支持WAV, AMR 和MIDI铃声，WAV,MIDI文件可通过红外或蓝牙接收，AMR文件由录音功能录制 <br>\r\n连接方式 红外和蓝牙 ：内置蓝牙晶片和红外线传输，图片、游戏、其它软件可以无线与PC连接交换数据 <br>\r\n中文短信息收发：结合合图片、文字和音效，透过MMS或是email传送给手机、PDA或是PC　 <br>\r\n透过GSM、HSCSD和GPRS收收发电子邮件 <br>\r\n电子邮件支持：SMTP、POP3、IMAP4　 <br>\r\n数码相机[内置] VGA（640 x 480）分辨率以及1670万色（24位） <br>\r\n拍摄模式有三种，Standard，Portrait和Night <br>\r\n内建相片簿 <br>\r\n图片支持格式：支持JPG、 BMP、GIF等格式的图片 <br>\r\n图象质量有三级设置，分别是High,Normal,Basic <br>\r\n4MB记忆体可储存图片、游戏等　 <br>\r\n内置镜头[可拍摄24bit数码相片]　 <br>\r\n声音:支持MIDI，WAV 和 AMR格式的声音文件和免提通话功能 <br>\r\n存储空间:3.5兆动态用户存储空间 <br>\r\n自动双频 <br>\r\nWAP　1.2.1 <br>\r\n中文输入 <br>\r\n中文短信息收发 <br>\r\nMMS多媒体短消息 <br>\r\n日期时钟 <br>\r\n内置振动 <br>\r\n翻盖[推盖式]　 <br>\r\n语音拨号[25个]　 <br>\r\n录音:每段录音1分钟 <br>\r\n可选铃声 <br>\r\n和弦铃声[24和弦] <br>\r\n自编铃声 <br>\r\n无线下载功能[下载铃声、图片、游戏等] 　 <br>\r\n五种情景模式，分别是General(普通)，Silent(宁静)，Meeting(会议)，Outdoor(户外)和Pager(传呼机) <br>\r\n待机图案和快捷键、显示屏的对比度、颜色方案、屏保时间和模式可调 <br>\r\n含有光线探测器 <br>\r\n话机电话簿可存储电话 <br>\r\n内置游戏[4种]:贪食蛇、纸牌、Mix Pix、Ergonomic game controls <br>\r\n闹钟 <br>\r\n计算器 <br>\r\n日历(三种视图模式:日,周,月)　 <br>\r\n待办事项(三级紧急程度) <br>\r\n货币换算 <br>\r\n换算器(汇率,面积,能量,长度,体积,压力,温度,时间等) <br>\r\n待机图案和快捷键、显示屏的对比度、颜色方案、屏保时间和模式可调，含有光线探测器 <br>\r\n两种时钟格式，一是传统械表格式，一种是电子表格式 <br>\r\n可配置蓝耳机 <br>\r\n来电图片显示、特定铃声 <br>\r\n<br>\r\n配件：一锂电、一直充、光盘、包装盒子、说明书（另加一原装锂电池需100元人民币~在外面购买市价要三百多~）<br>\r\n<br>\r\n<br>\r\n<br>\r\n能想象一下你和对方可视移动通话吗？全球首款带摄像功能的手机7650就能满足您。 <br>\r\n<br>\r\nNOKIA 7650 的影像功能组件包括内建式数码相机、可供储存相片的相片簿、和 176x208 像素的大型彩色屏幕。多媒体信息的运作方式与短信很类似，但多媒体信息可容许使用者将声音、图形、文字、和影像内容结合于一则信息中。一旦使用者选择好相片加上写好的文字、并附上声音档，便可将这个多媒体信息直接传送至另一台具多媒体信息功能的移动电话，或是传送至收件人的电子邮件信箱。先进的图像式使用者接口和四个方向的操纵摇杆，更为 NOKIA 7650 增添了使用上的便利与速度。", "0", "138", "2002-12-07 00:00:00", "pic/7650.jpg", "不详", "3750", "5490", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "True", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("176", "31", "4", "诺基亚8910", "3999", "真正的镁合金机壳!独特的弹出键盘设计!太炫了的机!一旦拥有别无索求!&amp;nbsp;", "网络类型：EGSM900/GSM1800/HSCSD <br>\n重量：110 克 <br>\n尺寸/体积：103-140.5×46×20mm/83cc <br>\n通话时间：120-240分钟 * <br>\n待机时间：100-300小时 * <br>\n<br>\n<br>\n【主要功能】 <br>\n<br>\n钛金属外壳是Nokia 8910最显著的特色之一 <br>\n钛金属一向以重量轻、质感佳著称 <br>\n流畅设计同时也反应在其创新的握钮，只要轻轻一按便可使外壳高雅地自动滑开，展现光亮的镀铬按键 <br>\n高分辨率灰阶屏幕在精致的白色背光灯衬托下也更为清晰 <br>\n自动双频 <br>\n内置天线 <br>\n翻盖[滑盖式] <br>\nGPRS网络和蓝牙 <br>\n用户可以通过WAP浏览更为先进的HSCSD网络，享受更为迅速方便地使用移动服务 <br>\n蓝牙技术也有助于与电脑的互连，实现更为快捷、方便的同步资料共享 <br>\n中文输入[预想拼音输入法+笔划输入法] <br>\n中文短信息收发 <br>\n日期时钟：时尚的指针式和数字式时钟全屏屏保选择 <br>\n内置振动 <br>\n录音[180秒] <br>\n语音拨号 <br>\n支持最大6方通话 <br>\n可选铃声 <br>\n情景模式 <br>\n通话时间提示 <br>\n话机电话簿可存储[500个]电话 <br>\n型动态内存，能记录多达500个电话簿资料，每个联系人下可存多个项目和电话号码 <br>\n通话记录[20个]已接、[10个]未接、[10个]已拨电话 <br>\n个性化提示音可使来电区分更加方便 <br>\n个人来电识别铃声 <br>\n动画屏保：可节约手机耗电的屏幕保护 <br>\n内置游戏 <br>\n红外线接口 <br>\n内置Modem <br>\nEFR、STK服务 <br>\n闹钟 <br>\n日历 <br>\n计算器 <br>\n记事本 <br>\n通过应用广泛的手机计算机编辑软件，轻松地更新电话簿、记事历、与待办事项，或从个人计算机上管理情境操作模式、电话簿与简讯 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\n这款手机比黄金外壳的8850更豪华。8910采用广受欢迎的全球行动通讯（GSM）标准，音质清晰宛如莫札特的交响乐。它除了有GPRS和蓝牙功能以外，这款手机还具有WAP浏览功能。8910通话时间为4小时，有300小时的待机时间，重110g。目前为止，8910很可能是最豪华、最高级的移动电话 <br>\n<br>\n如果说诺基亚5510算是另类，那么今天我们接触到诺基亚8910就完全是所谓雍容高贵的。诺基亚8910采用纯色的钛金属外壳设计，自然拥有有极佳的金属质感，并且将钛金属轻盈坚固的本质展现得淋漓尽致，机身体重（含电池）仅110克，时尚便携。 <br>\n<br>\n　　 <br>\n诺基亚8910依旧沿用着滑升式的机身设计，机身长度在闭合时为103毫米，完全打开时为140.5毫米，显得十分的修长。当随着机身的完全打开，将诺基亚8910靓丽的镀铬按键展现在你眼前的时候，它呈现的又是一种高雅的感觉。轻触按键，诺基亚8910独特的白色背景灯和高分辨率显示屏，又将清晰自然的视觉效果显现眼前。 <br>\n<br>\n　　 <br>\n诺基亚8910兼容EGSM900/GSM1800、GPRS和HSCSD多种网络类型，并且支持红外线和蓝牙等无线传输技术，让你无论在任何环境都能轻松的接入网络，享受更为迅速和方便的移动网络服务。 <br>\n<br>\n　　 <br>\n诺基亚8910采用高容量的动态内存，可以存储多达500个联系人，每个联系人项下可存储多个号码和文字项。个性化振铃音，可以让你方便地识别来电信息。语音拨号、最长3分钟语音录制，以及最大支持6方通话等，诺基亚8910完备强大的功能绝对不会让你失望。同时，诺基亚8910还有着良好的联机性能，方便不习惯手机键盘输入信息的用户在计算机上更新手机内的各种信息，或者从计算机上获取下载资讯等。", "0", "0", "2002-12-07 00:00:00", "pic/8910.jpg", "不详", "3949", "5680", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("178", "31", "4", "诺基亚6610", "2500", "６６１０-将商务融入生活", "网络类型：GSM900/1800/1900MHz,GPRS <br>\r\n重量：84 克 <br>\r\n尺寸/体积：106×45×17.5mm/71cc <br>\r\n通话时间：120-300分钟 * <br>\r\n待机时间：150-300小时 * <br>\r\n语言支持：中文简体 <br>\r\n<br>\r\n<br>\r\n【主要功能】 <br>\r\n<br>\r\n自动三频 <br>\r\nGPRS <br>\r\n内置天线 <br>\r\n可更换外壳[深蓝、绿、葡萄红] <br>\r\n彩色屏幕:可显示4,096种颜色 <br>\r\n超大彩色屏幕：128×128像素 <br>\r\nWAP 1.2.1 <br>\r\n4方向的滚动键 <br>\r\n中文输入[T9] <br>\r\n中文短信息收发 <br>\r\n话机SMS短信存储（最多75个文字短信或25个图片短信，图片预置10个，最多可保存35个平均大小为10K的图片） <br>\r\nMMS多媒体短消息存储（最多12个最大30K的MMS短信） <br>\r\n日期时钟 <br>\r\n内置振动 <br>\r\n内置免提扬声器 <br>\r\nMIDI（合成音）铃声（预置31个,用户自选空间20个） <br>\r\n可选铃声[31种] <br>\r\n全新合成音铃声可通过MMS接收，通过WAP或使用Nokia PC套件 5.0通过互联网从Club Nokia**下载 <br>\r\n无线下载功能[下载铃声、图片、游戏等] <br>\r\n情景模式 <br>\r\n话机电话簿可存储[300个]电话 <br>\r\n通话时间提示 <br>\r\n通话记录 <br>\r\n免提通话 <br>\r\n动画屏保（时钟式） <br>\r\n图形菜单 <br>\r\n内置游戏:可下载互动游戏 <br>\r\n红外线接口 <br>\r\n内置Modem <br>\r\nEFR、STK服务 <br>\r\n闹钟 <br>\r\n定时器 <br>\r\n日历（最多可设250条备忘） <br>\r\n计算器 <br>\r\n秒表 <br>\r\n记事本（最多30条记录） <br>\r\n自动键盘锁 <br>\r\n世界时钟 <br>\r\n货币换算 <br>\r\n内置立体声调频收音机 <br>\r\n转换器（汇率、气温、重量和其他度量衡） <br>\r\n协议管理器（监测股票和其他证券） <br>\r\nJava游戏 <br>\r\n支持专为小型消费电子产品设计的J2ME技术（Java 2 微型版本）（预置3个应用软件，最多可有6个大小为64K的应用软件） <br>\r\n收发E-Mail <br>\r\n<br>\r\n配置：一电一充 包装，说明书！<br>\r\n<br>\r\n<br>\r\n<br>\r\n这款令人心动的手机专为移动专业人士打造，可用性相当高。它拥有高分辨率彩色显示屏,支持可下载应用的Java(技术、MMS*(移动多媒体信息)与MIDI(合成音)铃声，内置免提扬声器、立体声调频收音机，还有一个4方向的滚动键。 <br>\r\n<br>\r\n诺基亚6610支持专为小型消费电子产品设计的J2ME(技术(Java 2微型版本)。诺基亚6610的Java平台基于MIDP 1.0(移动信息设备模式)，MIDP 1.0允许应用开发商为移动设备开发诱人的创新应用。J2ME让用户能够下载一系列Java应用，包括互动和图形游戏、动态商业应用和终端增强软件。诺基亚6610预置的Java应用包括转换器(汇率、气温、重量和其他度量衡)、协议管理器(监测股票和其他证券)和Java游戏。 <br>\r\n<br>\r\n诺基亚6610基于诺基亚40系列用户界面，这一界面是专为拥有彩色显示屏的小型手机优化的。诺基亚6610的高分辨率彩色显示屏支持4096种彩色显示，为WAP浏览、日历查看和电话簿功能带来了更佳视觉效果，同时丰富了Java应用、移动多媒体信息和游戏的使用体验。 <br>\r\n<br>\r\n诺基亚6610的其他诱人功能包括MIDI(合成音)铃声、内置立体声调频收音机，为用户提供了高质量的声效体验。合成音铃声和传统铃声都可以用作个性化振铃音和短信息提示音。全新合成音铃声可通过MMS接收，通过WAP或使用Nokia PC套件5.0通过互联网从Club Nokia**下载。诺基亚6610的内置免提扬声器可以让用户召开电话会议，或者在收听广播时无需佩戴耳机***。", "0", "0", "2002-12-07 00:00:00", "pic/6610.gif", "不详", "2500", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("179", "30", "4", "爱立信T65", "760", "诱人的曲线Ｔ６５", "网络类型：GSM900/1800和E-GSM、GPRS <br>\n重量：94 克 <br>\n尺寸/体积：105×49×21mm <br>\n通话时间：150-660分钟 * <br>\n待机时间：160-300小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n屏幕显示：4种灰度层次效果，36.4×29毫米 <br>\n解析度：101×67像素 <br>\n6行英文显示，3行中文加提示行大屏幕显示 <br>\n内置式天线及电池 <br>\n曲线优美，设计时尚 <br>\n多项有趣的个性化设置和信息功能选择 <br>\n一键上网令你即时连接移动互联网 <br>\n可选择你所喜爱的WAP网页作为预设网页 <br>\n轻松连接及享用各项移动互联网服务和内容 <br>\n收发电子邮件 <br>\n上网速率最高可达43.2kbps <br>\n高效率的移动互联网浏览器 <br>\n快速电邮及文件下载 <br>\n自动双频 <br>\nGPRS（通用分组无线业务） <br>\n中文输入[预想式笔画智能输入法] <br>\n中文短信息收发 <br>\n通过短消息服务，能令用户发送和接收图片、动画、声音标签、铃声信号和格式化文字（例如粗体设定） <br>\n个性化自设功能，用户可以用图像、声音，以及从互联网上下载更新的图像及声音内容来设定手机，充分展现自我的风采 <br>\n可从移动互联网的WAP网站下载音乐和图片，并储存在手机里 <br>\n内置天线 <br>\n日期时钟 <br>\n内置振动 <br>\n语音拨号[50个] <br>\n语音接听 <br>\n可选铃声[15种] <br>\n自编铃声[8种] <br>\n情景模式 <br>\n通话时间提示 <br>\n话机电话簿可存储[300个]电话 <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\n自由设定通讯录为短清单和完整清单 <br>\n图片电话簿：可在电话簿中预设来电者图片， <br>\n一看图像即知来电者是谁 <br>\n背景图像设置 <br>\n通过电话簿快速查询号码 <br>\n按音量键可以实时查按看待机时间及通话时间 <br>\n移动聊天功能 <br>\n内置游戏[1种] <br>\n通过WAP可以在网上下载待机大图 <br>\n无线下载功能[下载铃声、图片] <br>\nEFR、STK服务 <br>\n闹钟:关机仍可使用 <br>\n计算器 <br>\n秒表 <br>\n记事本 <br>\n世界时钟 <br>\n自动时区 <br>\n自动键盘锁 <br>\n顶置卡仓，方便独特 <br>\n配合爱立信CommunicamTM,T65便成为了一个数码照相机，用户可以随时随地拍摄数码照片，并作为一个电子邮件附件发送，或者储存在互联网上的电子相册中与朋友分享 <br>\n手机内置76种生动活泼图片，6种动画和15段响铃音乐，可加插在发送德短消息中。 <br>\n可自设30种我的图片和8种音乐。 <br>\nPIM个人信息管理，可与PC更新日程表、任务及电话。 <br>\n“魔术词”――预设语音直接拨叫、语音接听及转换个人化模式，无须按键。（需使用免提通话装置） <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\nT65一改爱立信的手机传统，以诱人的曲线并赋予亮丽的色彩，再搭配内建的隐藏式天线，打造出的全新外型。加上一次显示六行英文、四行中文的超大的屏幕，方便使用者浏览网络及收发电子邮件。并别出心裁地在手机顶端加上 SIM 卡插槽，安装 SIM 卡不需打开背盖。T65 也内建了多项先进功能，其中包括搭配使用爱立信 MCA-10 数字相机、最具个人化色彩的屏幕背景图片和相片式的通讯簿，更方便快速的让您寻找亲朋好友的资料。当然，T65 也有各种网络下载的功能让您可以有更多的选择，让生活中充满乐趣。 <br>\n<br>\nT65 支持 GPRS，可提供单键行动上网，同时更可提供高达 43.2 kbps 快速行动网际网络联机速度，让使用者无论收发 e-mail 或下载档案，都能轻松享受飙网的快感。只要在待机模式下按住蓝色存取键，便能立刻连结到个人首页。您可从首页造访其它网站，随时寻找信息或下载手机图案或铃声。T65 支持 GPRS 技术，因此能随时移动上网。", "0", "0", "2002-12-08 00:00:00", "pic/t65.jpg", "不详", "760", "1750", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("180", "30", "4", "爱立信T20", "580", "年轻族群追求Ｔ２０", "网络类型：GSM900MHz/DCS1800MHz <br>\n重量：128 克 <br>\n尺寸/体积：101×54×28mm <br>\n通话时间：210-300分钟 * <br>\n待机时间：85-200小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 <br>\nWAP上网 <br>\n中文输入[拼音、注音和笔划键盘输入方式] <br>\n中文短信息收发 <br>\n日期时钟 <br>\n内置振动 <br>\n语音拨号[10个] <br>\n语音接听：配合免提设备使用 <br>\n可选铃声[4种] <br>\n音乐铃声[11种] <br>\n自编铃声[4种] <br>\n自定义情景模式[6种] <br>\n通话时间显示及分钟提示 <br>\n话机电话簿可存储[100条]电话并可存储EMAIL地址 <br>\n通话记录[10个已接、10个未接、10个已拨电话号码并带时间或日期] <br>\n内置游戏[4种]：俄罗斯方块、打方砖、类似天Q变、小精灵 <br>\n支持EFR、STK服务 <br>\n闹钟[关机有效] <br>\n计算器 <br>\n倒计时器 <br>\n秒表 <br>\n世界时钟 <br>\n开/关式功能翻盖 <br>\n有吊饰孔 <br>\n可外接MP3播放器 <br>\n可外接爱立信FM收音设备 <br>\n可外接免提耳机 <br>\n可外接聊天板 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\nT20除了跟T28一样支援声控拨号，同时还新增WAP上网、行动聊天室功能。首先在WAP方面，T20为支援的是WAP1.1版，因此是一支WAP手机，最大上网传速为9.6Kbps。聊天功能则是类似短消息收发功能，虽然不像NOKIA 3310可以同时在萤幕上显示双方对话，不过使用者仍可以利用T20输入文字短消息，然后同时传送给多位或单一对象。易利信与瑞典Mgage Systems公司共同推出WAP-to-WAP、WAP-to-web及web-to-WAP的即时移动聊天机制，爱立信并将其移动网络的WAP入口网站(WAP-portal)提供这些服务。使用者可以依据自己的偏好与兴趣来选择不同的聊天室。聊天时，可以匿名或显示自己的姓名。此外，也可以建立私人聊天室，与自己的朋友一对一交谈。另外，Mgage Systems也开发行动e-card服务，使用者可以利用WAP-to-WAP或WAP-to-email方式传送电子卡片。", "0", "0", "2002-12-08 00:00:00", "pic/t20.jpg", "不详", "580", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("184", "30", "444", "爱立信T29", "780", "T29外壳采雾面喷砂的金属质感设计，配以手机盖两侧的弧度，具有时尚流行风格。", "网络类型：GSM900/1800MHz <br>\n重量：95 克 <br>\n尺寸/体积：95×51×22mm <br>\n通话时间：210-420分钟 * <br>\n待机时间：85-150小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 <br>\n支持翻盖功能 <br>\nWAP上网 <br>\n中文输入[拼音和笔划键盘输入方式] <br>\n中文短信息收发：预设常用短语 <br>\n日期时钟 <br>\n内置振动 <br>\n语音拨号[10个] <br>\n语音接听：配合免提设备使用 <br>\n可选铃声[11种] <br>\n自编铃声[4种] *[8种] <br>\n情景模式 <br>\n通话时间提示 [每隔一分钟提示一次] <br>\n话机电话簿可存储[100条]电话并可存储EMAIL *[可以存储200条电话] <br>\n通话记录[10个已接、10个未接、10个已拨电话号码并带时间或日期] <br>\n内置游戏[4种]：小精灵、俄罗斯方块、躲避球、打砖块 *[增加一种飞机游戏] <br>\nEFR、STK服务 <br>\n闹钟 [支持关机闹钟] <br>\n计算器 <br>\n计时器 <br>\n秒表 <br>\n世界时钟 <br>\n开/关式功能翻盖 <br>\n开机动画显示：可自订开机和关机时的动画与音乐 <br>\n可接&quot;蓝牙&quot;耳机 <br>\n可接MP3播放器 <br>\n可接爱立信FM收音设备 <br>\n可接爱立信聊天板(chatboard) <br>\n银灰色、银蓝色外壳分别搭配水晶紫和宝石蓝的珐琅镶嵌板 <br>\n*支持EMS短信模式。可以借助短信功能发送图片与铃声 <br>\n*移动聊天室 <br>\n*内置编辑图片功能 <br>\n*短信回执功能 <br>\n(带*的为R3版T29新增功能) <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\nT29外壳采雾面喷砂的金属质感设计，配以手机盖两侧的弧度，具有时尚流行风格；掀开话盖则呈现深色镶边及怠色柔和的透明发光按键。T29有两种颜色，分别是银灰色及银蓝色外壳，各自搭配水晶紫和宝石蓝的深色珐琅镶嵌板。整体造型轻薄小巧，合乎人体工学，其重量包含超薄电池在内只有95克。 <br>\n<br>\nT29品质优良，效能卓越，还有多种引人注目的功能，例如：T29可用於传送简讯 (SMS)及群组简讯讯息，也可将常用的讯息内容储存为范本，让您传送讯息时更为轻松快速。T29包含四种游戏(小精灵、俄罗斯方块、躲避球、打砖块)、开机动画显示、声控拨号、情境模式设定、来电震动、自编铃声和详尽的电话簿，所有行动电话中最实用、最受欢迎的功能一应俱全；另外，T29的掀盖式应答设计，让使用者可以最简单的方式进行通话；同时为了让使用者可以透过小巧的手机轻松读取萤幕文字，萤幕上的图形显示都是经过特别设计，所有的功能皆透过直觉式的图形功能目录系统，在操作上更为灵活便捷。 <br>\n<br>\nT29 的待机时间长达150小时(约6.5天)，通话时间也有7小时。此外，消费者可以自订开机和关机时的动画及音乐，享受个人风格。由於手机配件愈来愈流行，T29也可随意地搭配易利信的配件，来扩充手机的功能及多元性，这些配件包括蓝芽耳机、FM收音机免持听筒配备、MP3、聊天板(chatboard)、车用手机座和各种免持听筒的配件。", "0", "1", "2002-12-08 00:00:00", "pic/t29.gif", "不详", "780", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("185", "30", "4", "爱立信T39", "1000", "爱立信&nbsp;T39mc&nbsp;适用于欧洲、亚太地区和美洲地区的&nbsp;GSM&nbsp;900/1800/1900&nbsp;网络系统─也就是说，世界各地&nbsp;GSM&nbsp;频率所及之地均可使用。此款手机还支持&nbsp;GPRS，可进行快速资料传输，并内置蓝牙&nbsp;(Bluetooth)&nbsp;无线技术和先进的个人信息管理&nbsp;(PIM)&nbsp;应用程序，如日程、通讯录、备忘录等，以及各种创新功能。", "网络类型：GSM900/1800/1900、GPRS <br>\n重量：86 克 <br>\n尺寸/体积：96×50×18mm <br>\n通话时间：150-660分钟 <br>\n待机时间：150-300小时 <br>\n<br>\n【主要功能】 <br>\n<br>\nGSM900、1800、1900三频自动切换 <br>\n内置蓝牙(Bluetooth)无线技术功能 <br>\n内置蓝牙芯片 <br>\n不用接驳器就可以用到 Bluetooth 耳筒 <br>\n支持高速资料传输(HSCSD) <br>\n5行高清晰灰度显示屏、中文显示3行 <br>\n开/关式功能翻盖 <br>\n支持GPRS 1+3 约 50kbps 的传送速度 <br>\nHSCSD 28.8kbps 速度 <br>\nWAP1.2.1浏览器 <br>\n收发 POP3 /SMTP E-mail了 <br>\n中文输入[预想式T9智能输入法(T39M)] <br>\n中文短信息收发 <br>\n支持长短信息(Long Messages) <br>\n支持EMS，可直接接受网上发送的铃声及图片功能(T39M) <br>\n日期时钟 <br>\n内置振动 <br>\n录音[122秒]：分为高保真32秒和普通90秒 <br>\n语音拨号[10个] <br>\n语音接听 <br>\n免提通话装置(需另外购买) <br>\nMP3免提通话装置(需另外购买) <br>\nFM调频收音机通话装置(需另外购买) <br>\n车内免提通话装置(需另外购买) <br>\n可选铃声[14种] <br>\n自编铃声[8种] <br>\n情景模式 <br>\n通话时间提示 <br>\n话机电话簿可存储[510个]电话 <br>\n多记忆的电话簿 <br>\n个人资料管理 <br>\nPIM能与电脑笔记本电脑或PDA进行同步资料化 <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\n可设置为屏幕休眠，更省电 <br>\n内置游戏[3个]：Erix撞砖及、Tennis打网球及贪食蛇(隐藏游戏) <br>\n红外线接口 <br>\n内置Modem <br>\nEFR、STK服务 <br>\n闹钟:关机仍可使用 <br>\n日历 <br>\n计算器 <br>\n秒表 <br>\n记事本 <br>\n世界时钟 <br>\n[Background Pictures]功能:6个内置图案，一个自编图案 <br>\n书签 (URL记忆库) <br>\n接通线路识别显示(COLP) <br>\n屏幕灯 <br>\n电子邮件记忆内存 <br>\n外部天线连接器 <br>\n计时器 <br>\nSyncML 数据更新标准 <br>\n与电脑同步(最低配置: Win?98，NT4.0，2000或WinME) <br>\n用户组群通话 <br>\n3级WTLS <br>\n增强全速率/全速率与半速率语音编码 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\n爱立信 T39mc 支持GSM 900/1800/1900 世界各地 GSM 频率所及之地均可使用。此款手机还支持 GPRS，可进行快速数据传输，并内建蓝芽 (Bluetooth) 无线技术和先进的个人信息管理 (PIM) 应用程序，如行事历、通讯簿、备忘录等，以及各种创新功能。　 <br>\n<br>\nT39mc 支持GPRS ，同时也可提供更快速行动网际网络联机速度。通过 T39mc 内建的调制解调器，使用者便能享受“永不中断”的行动网际网络联机，具有瞬间发送电子邮件、高速网际网络存取等功能，并能和企业 LAN 建立有效率的联机机制。T39mc 可支持高速数据传输 (HSCSD)，这种技术可令 T39mc 迅速连到网络，速度远比一般 GSM 联机快上好几倍。HSCSD 与 GPRS 技术的结合，让行动电话能进行较大型档案的数据传输。 <br>\n<br>\n透过内建的蓝芽无线技术，T39mc 便能和蓝芽耳机、笔记型计算机、PDA 或其它装置以无线电联机方式进行通讯。T39mc 的划时代技术，在待机时间和通话时间上也提供了极为强大的效能。它的待机时间高达 300 小时 (约12天)，通话时间则长达 11 小时。 <br>\n<br>\n个人信息管理(PIM) 应用程序也是 T39mc 备受瞩目的内建特性。PIM 能让您运用系统化方法来建立个人通讯簿、行事历和工作项目，和计算机上的操作极为相似。先进的 T39mc 行事历可让您储存约会、出席者与工作内容的详细资料。PIM 应用程序也能透过 T39mc 的多重连接功能，和计算机、笔记型计算机或PDA进行同步资料化。而内建的 2 组游戏中，除了俄罗斯方块之外，新加入的网球高手游戏可单人与手机挑战，还可以透过蓝芽功能与朋友进行联机对打游戏。", "0", "0", "2002-12-08 00:00:00", "pic/T39.gif", "不详", "1000", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("186", "30", "4", "爱立信R600", "1180", "R600是一款功能强大的手机，它具备增强型信息、快速移动互联网访问和更多的个性化功能。其中，它的快速上网功能将被得到广泛的应用。", "网络类型：GSM900MHz/1800MHz,GPRS <br>\n重量：82 克 <br>\n尺寸/体积：105×45×20mm <br>\n通话时间：120-240分钟 * <br>\n待机时间：90-150小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 <br>\n内置天线 <br>\n显示屏幕:4种灰度层次效果，32.88×21.76毫米 <br>\n解析度：101×67像素 <br>\n大显示屏（4行） <br>\n3种颜色背景光-红、绿和橙 <br>\n其屏幕显示颜色可以被设定为绿色、红色或橙色 <br>\n而且可以被用作屏幕背景颜色或确认来电者的显示颜色 <br>\n您可以根据电话簿中不同联系人预设不同的背景颜色显示，并通过颜色显示来判断是谁的来电 <br>\n高速和GPRS永远在线 <br>\n支持最高达43.2kbps的连接速度 <br>\n高效的移动互联网浏览器 <br>\n最多可存储25个书签 <br>\n配备2级WTLS的WAP1.2.1版本 <br>\n增加了WAP安全性以实现更安全的在线购物、银行服务和电子商务 <br>\n中文输入[预想式T9智能输入法] <br>\n中文短信息收发 <br>\n通过EMS传输图片、音乐和格式化文字，让文字短消息更具表现力 <br>\n可预设音乐和图片 <br>\n与其它支持EMS功能的手机互换铃声 <br>\n通过短消息功能，可在支持移动聊天的手机上实现点到点即时信息传输的移动聊天 <br>\n在进行移动聊天时，之前接收和发送的信息仍显示在屏幕上 <br>\n日期时钟 <br>\n内置振动 <br>\n可存储从移动互联网的WAP网页下载的个性化声音和图片 <br>\n无线下载功能[下载铃声、图片] <br>\n通话时间提示 <br>\n话机电话簿可存储[200个]电话 <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\n背景图片[5幅] <br>\n背景灯颜色[绿、红、橙] <br>\n内置游戏[3个] <br>\n内置Modem <br>\nEFR、STK服务 <br>\n日历 <br>\n月、周和日的日历显示 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\nR600是一款功能强大的手机，它具备增强型信息、快速移动互联网访问和更多的个性化功能。其中，它的快速上网功能将被得到广泛的应用。 <br>\n　　 <br>\n爱立信的设计理念将这款R600表现得淋漓尽致。首先，它小巧的机身采用流线型设计，典雅大方，颇具大家风范。天线内置，更节省空间；加大显示屏设计，每屏可显示五行，使上网和操作更为得心应手。R600共有香槟色和冰河蓝两种颜色可供玩家选择。 <br>\n　　 <br>\nR600的亮点之一就是它的增强信息服务(EMS)，用户就需操作很少的几个按键就可以随心所欲地收发带有图片、动画和声音的信息。除了可以使用该机内带的图片、动画和声音外，用户也可以添加自己喜欢的图片、动画和声音资料。 <br>\n　　 <br>\n对GPRS网络的技术使R600的娱乐功能如虎添翼，用户可以利用它快速地从互联网上下载各种图片和音乐。它最高可支持43.2kbps的传输速率。 <br>\n　　 <br>\n此外，为了迎合时尚玩家的个性化需求，R600不负众望：它的显示屏背景灯颜色可以设置为绿色、红色或者橙色，而且可以电话簿的功能联系在一起，以此识别来电者的身份。在待机模式下，R600可以设置一个显示屏背影图片，使机主的个性化风格更为凸显。", "0", "6", "2002-12-08 00:00:00", "pic/r600.jpg", "不详", "1180", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
