INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("199", "32", "4", "摩托罗拉V60i", "2400", "摩托罗拉的ＭＰ３手机Ｖ６０i", "网络类型：GSM900/1800/1900MHz、GPRS <br>\n重量：112 克 <br>\n尺寸/体积：87×45×24mm/75cc <br>\n通话时间：315分钟 * <br>\n待机时间：225小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n自动三频 <br>\nGPRS <br>\n翻盖 <br>\n可更换外壳[可更换纯金属外壳] <br>\n显示屏幕：双屏显示屏幕外屏96×16像素，内屏96×64像素 <br>\nWAP 1.2.1 <br>\n中文输入[T9智能输入法] <br>\n中文短信息收发 <br>\n支持 EMS v4.0 标准、短消息聊天室和群组发送功能 <br>\n支持EMS音画短信 <br>\n简讯聊天室 <br>\n全新MMI人性操作介面 <br>\n日期时钟 <br>\n内置振动 <br>\n录音[60秒] <br>\n语音拨号[15个] <br>\n语音接听 <br>\n可选铃声[42种] <br>\n自编铃声[32种] <br>\n无线下载功能[下载铃声、图片、游戏等] <br>\n情景模式 <br>\n通话时间提示 <br>\n话机电话簿可存储[500个]电话 <br>\n通话记录[20个]已接、[20个]未接、[20个]已拨电话 <br>\n来电识别[来电分组铃声功能] <br>\n图形菜单 <br>\n动画屏保 <br>\n内置游戏[5个] <br>\n可下载四个JAVA游戏 <br>\n内置Modem <br>\nEFR、STK服务 <br>\n闹钟 <br>\n日历 <br>\n计算器 <br>\n记事本 <br>\n货币换算 <br>\n搭配专用配件FM收音机功能 <br>\n电话簿及SIM卡复制功能 <br>\n自订功能捷径 <br>\n支持调频立体声播放 <br>\n支持MP3音乐播放 <br>\n<br>\n配置：一锂电、一直充、耳机<br>\n<br>\n<br>\n<br>\n从V60到V60+，再从V60+到V60i，摩托罗拉V系列手机不断散发出诱人的魅力。V60i有星空蓝和银河灰两种机身色彩可选，可更换纯金属彩色外壳，外观有着极强的质感，非常的个性和时尚。它采用双屏翻盖设计，双屏来电显示，机身尺寸约87*45*24毫米，重112克，使用舒适自然。相信摩托罗拉V系列的拥护者一定会一如既往的青睐V60i。 <br>\n<br>\nV60i支持GSM 900/1800/1900MHz自动三频和GPRS无线网络，以及WAP网络浏览和J2ME JAVA程序下载运行等，强大的功能着实让人心动；新增EMS音画短信功能和声控拨号功能，把沟通变得更为亲切自然；内置42种可选铃声，32项自编铃声，随时感受新的声音；升级了大容量的机身电话簿，可存储500个电话号码，并支持分群组管理；还有非常个性的使用设定，可自定义菜单顺序，让一切变得更合心意；内置5款手机迷你游戏，并支持4个JAVA游戏下载，很适合休闲消遣；其他的，还包括个人信息管理、定时提示、语音记事等相当不错的实用功能；另外更可外接FM立体声调频耳机或MP3数字音乐耳机（需单独购买），深度体现V60i的无穷魅力。", "0", "0", "2002-12-08 00:00:00", "pic/v60i.gif", "不详", "2370", "3100", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("201", "32", "4", "摩托罗拉V70", "2500", "摩托罗拉出的360度旋转V70", "网络类型：GSM900/1800MHz <br>\n重量：79 克 <br>\n尺寸/体积：94×38×18.3mm <br>\n通话时间：110-130分钟 * <br>\n待机时间：50-140小时 * <br>\n<br>\n<br>\n【主要功能】 <br>\n<br>\n突破常规的独特外形，颠覆设计因我不同 <br>\n360度旋转开盖，凡俗模式，从此突破 <br>\n深海蓝背景显示屏，神秘气息，无与伦比 <br>\n半透明键盘面板，极昼折，冰川蓝，极夜蓝三种荧光色 <br>\n机身轻薄小巧，掌心可藏 <br>\n全新用户界面，方便快捷，体贴入微 <br>\n自动双频 <br>\n可更换外壳[显示屏环]，金属气质，卓然不群 <br>\nWAP 1.2.1 <br>\n4+1GPRS[传输速度84kbps，可外挂蓝芽配适器] 　　 <br>\n中文输入[CKE全中文键盘输入] <br>\n中文短信息收发 <br>\n可选铃声[32首] <br>\n自编铃声[32首] <br>\n内置震动 <br>\n话机电话簿可存储[500 组电话] <br>\n一人名下，多组电话(家/办公室/手机) <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\n语音拨号[25组]　　 <br>\n日历、日期及时间显示　　 <br>\n内置游戏[3个]：21点，考脑力，反弹球 <br>\n内置日程表[最多可记录500组资料] <br>\nEFR、STK服务 <br>\n闹钟功能　　 <br>\n计算器 <br>\n货币换算 <br>\n资料同步使用 USB 传输线　　 <br>\nCD-ROM Starfish TrueSync 光碟内包含 GPRS Driver 及同步更新软件 <br>\n支持调频立体声播放 <br>\n<br>\n配置：一锂电、一直充、说明书、包装<br>\n<br>\n<br>\n<br>\n凭借超越时尚、独具匠心的设计风格，摩托罗拉V70一亮相就征服了所有消费者的心，它对当今时尚的震撼完全印证了产品倡导的口号――&quot;世界因我而不同&quot;。它拥有轻巧的机身和磨光金属色外壳，外形线条充满强烈视觉冲击力和独特创意。更加与众不同的是其特有的接听开盖方式，可随心360度旋转的接听开盖设计灵感来自于东方的折扇。 <br>\n　　 <br>\n摩托罗拉革命性的设计灵感在整部手机上得以完美演绎，其特大液晶屏幕以深海蓝的背景配合白色输入显示，再加上多色可置换屏幕外环，酷感十足。而蓝色暗光背景键盘在黑暗中如同深海夜钻，令人心动不已。整部手机设计动静结合，对于永远追求最新时尚，张扬自我个性与独特品味的人士充满难以形容的吸引力。 <br>\n　　 <br>\n与外形设计超时尚魅力相得益彰的是，V70手机功能设置同样充分体现了摩托罗拉先进的通信科技。V70手机支持GPRS技术，其个性化功能设置独具品味。如自定义菜单功能，菜单顺序可根据个人喜好设置；32种设定铃声和32种可编辑铃声让你的沟通更加动听；多达500个电话号码储存功能令时尚生活方便自如；群组短信息功能支持同时多人发送。此外，V70手机还拥有语音拨号、同步数据传输、日程表、日历和轻松游戏等。", "0", "25", "2002-12-08 00:00:00", "pic/v70.gif", "不详", "2480", "3650", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "True", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("202", "33", "4", "索尼爱立信T202", "1150", "图的就是好玩Ｔ２０２", "网络类型：GSM,GPRS 三频 <br>\n重量：85 克 <br>\n尺寸/体积：105×48×22mm <br>\n通话时间：180-780分钟 * <br>\n待机时间：140-220小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n三频手机 <br>\n内置天线 <br>\n显示屏幕 :屏幕背景灯光(蓝色) <br>\n可以通过GPRS网络和WAP1.2.1浏览器快速连接移动互联网 <br>\n高速数据传输(HSCSD, 高达28,8 kbps) <br>\n一按蓝键直驳上网 <br>\n中文输入[拼音,笔划输入法] <br>\n可外接数字相机 CommuniCam MCA-10，可将照片做为电子邮件的附件文件传送给亲朋好友，或上传至线上相簿网站加影像与个性化特色的 Picture Phonebook。 <br>\n中文短信息收发：EMS 简讯图片与铃声 <br>\n移动聊天功能 <br>\n个性化来电设置：能够将不同的来电者以图形显示，并储存于电话簿之中。无论接听来电或拨号出去时，此图形与姓名会自动显示于屏幕之中 <br>\n日期时钟 <br>\n内置振动 <br>\n语音接听 <br>\n可选铃声 <br>\n自编铃声 <br>\n无线下载功能[下载铃声、图片、游戏等] <br>\n情景模式 <br>\n通话时间提示 <br>\n图片电话簿 <br>\n话机电话簿可存储电话 <br>\n指定用户组群 <br>\n免提通话 <br>\n动画屏保 <br>\n图形菜单 <br>\n内置游戏[5个]:Ballpop,Q,Path,Pulldown和网球 <br>\n内置Modem <br>\nEFR、STK服务 <br>\n闹钟 <br>\n计时器 <br>\n日程表 <br>\n运行秒表 <br>\n内置日历功能可记录会议、纪念日、或其它不可忘记的事项 <br>\n快速上网键 <br>\n提供用于附挂腕带或吊饰的吊环 <br>\n自动键盘锁:键盘锁 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\n作为通讯工具，手机早已经在某种程度上成为了时尚一族诠释个性和独特品位的标志。而在到处充斥着个性标志的今天，要体现独特的时尚品位，恐怕也并非易事。也许，在大多数情况下，您都可以自如地使用丰富多彩的中文来传情达意，可是有时，文字和语言也会贫乏到不足以表达您的心意；也许您已经尝试用短信图片来代替语言，可是有时，您也难免会懊恼于短信图片的千篇一律和缺乏新意。那么，看一看T202和她的疯狂图库能为您做些什么吧： <br>\n<br>\n电话她不接，短信也发了几十条，可她还在生气，生气，生气……隔着千山万水，您仍然可以看到她心里的熊熊怒火。那就给她发送一只灭火器，“119”站在了眼前，她总不好意思让老房子继续失火吧？女友在那边终于忍俊不禁“阴转晴”，再从图库里挑一张面露羞涩的粉色“分分钟想你”发过来，也说不定哦？ <br>\n<br>\n朋友间开玩笑是常有的事，怎么才能显出您的与众不同呢？在疯狂图库里挑一条龇牙咧嘴的大鲨鱼，再跟上一句，“想吃腥？你试试！”，朋友顿时目瞪口呆，无言以对。哈，真有您的！ <br>\n<br>\n拥有索尼爱立信T202，拥有索尼爱立信的疯狂图库，您的幽默和个性，您的机智和敏锐才真正找到了合适的出口，交流才会充满生机和乐趣。虽然，图片传情并非您心爱的T202专利，可是，如此丰富的图库资源，充满前卫和幽默的设计，4000人在为您的图库出谋划策，除了您，谁还有资格拥有？", "0", "0", "2002-12-08 00:00:00", "pic/t202.gif", "不详", "1150", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("203", "33", "4", "索尼爱立信T602", "1750", "T602虽然机子小小但它却是一台具备三频功能的手机来，的确是不能够小看。T602内置了250个电话簿的存储空间，5款精彩的游戏更加为咱们中国的用户准备了一个可支持农历显示的日历可谓设想周到。", "网络类型：GSM 900/1800/1900MHz <br>\n重量：63 克 <br>\n尺寸/体积：92×41×20mm/60cc <br>\n通话时间：90-300分钟 * <br>\n待机时间：80-180小时 * <br>\n<br>\n<br>\n【主要功能】 <br>\n<br>\n自动三频 <br>\n内置天线 <br>\nWAP 版本： 1.2.1 <br>\n中文输入[T9智能输入法] <br>\n中文短信息收发 <br>\n可支持图片与音效的 EMS 声动短讯 <br>\n日期时钟 <br>\n内置振动 <br>\n可选铃声[10种以上] <br>\n自编铃声[8种以上] <br>\n情景模式 <br>\n通话时间提示 <br>\n话机电话簿可存储[250个]电话 <br>\n通话记录[10个以上]已接、[10个以上]未接、[10个以上]已拨电话 <br>\n动画屏保 <br>\n图形菜单 <br>\n内置游戏[5款] <br>\nEMS功能同时已获得其他主要手机生产者 (如Motorola、Samsung、及Siemens) 运用在手机功能之中 <br>\n此外，T600 亦同时支持 Smart Messages 功能 <br>\nEFR、STK服务 <br>\n关机闹钟[支持每日闹、每周闹] <br>\n日历[支持农历显示] <br>\n计算器 <br>\n秒表 <br>\n记事本 <br>\n行事历 <br>\n自动键盘锁 <br>\n世界时钟 <br>\n货币换算 <br>\n电子名片 <br>\n收发E-Mail <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\nT602虽然机子小小但它却是一台具备三频功能的手机来，的确是不能够小看。T602内置了250个电话簿的存储空间，5款精彩的游戏更加为咱们中国的用户准备了一个可支持农历显示的日历可谓设想周到。 <br>\n<br>\nT602的机身纤细轻巧，外观更拥有超眩的金属质感，它的键位分布基本和T66差不多，而手机绳的挂孔就比较特别地设计在了手机的头顶，可能就是为了方便大家挂在胸前吧。T602这台手机虽然很小巧，但通话和待机的时间可一点也不短，分别达到了5个小时和180个小时 <br>\n<br>\nT602拥有10种以上的可选铃声与8种以上的自编铃声，虽然不是和弦，但是响度没问题，个性化也不错。游戏方面，T602内置的游戏增加到了5个之多，其中有赛车游戏和打弹珠等，这些游戏都能配合机身内部震动功能达到互动的效果，就和PS上常用的震动手柄差不多。 <br>\n<br>\nT602机身内置电话本容量为250个，虽然算不上大，但是对于普通的用户来说是足够的了，电话本功能中还有名片的功能，能存储一些除了电话以外的信息。爱立信对每个选项都进行了加注，如果光标在选项上停留一段时间，就会弹出滚动的简单说明书，对该选项的内容与用法加以描述，这对新上手时是个很好的功能。", "0", "0", "2002-12-08 00:00:00", "pic/t602.gif", "不详", "1750", "2500", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("204", "33", "4", "索尼爱立信T300", "2980", "T300拥有许多娱乐性很强的多媒体功能：如数码影像、短消息以及游戏等，价格定位大众市场。这部手机支持三频GSM和GPRS网络、彩屏、和弦立体声铃声以及多媒体短消息，是一部全功能的短消息手机。", "网络类型：GSM三频 <br>\n重量：101 克 <br>\n尺寸/体积：106×22×48mm <br>\n通话时间：360-450分钟 * <br>\n待机时间：300-350小时 * <br>\n中文支援： 繁体中文 <br>\n中文输入： 大陆拼音、笔划输入、T9英文 <br>\n<br>\n<br>\n【主要功能】 <br>\n<br>\n自动三频 <br>\nGPRS <br>\n内置天线 <br>\n可更换外壳[多种彩色]面板可供使用者依不同场合和服装氨搭配更换 <br>\n彩色屏幕：超大型 256 色彩色屏幕 <br>\n最新的 CommuniCam MCA-25 <br>\n丰富的影像功能 <br>\n您可以把拍摄的图片透过 MMS 传送出去、设定为手机屏幕保护程序、也可以直接在手机上面加以浏览 <br>\n拍摄的图片亦可依个人需求，将分辨率储存为最小 80x60 画素到最大的 640x480 画素等四种不同的尺寸。 <br>\n独特导航摇杆 <br>\nWAP <br>\n中文输入 <br>\n中文短信息收发 <br>\nMMS多媒体短消息 <br>\nMMS 收发包含彩色相片、动画、文字、音乐与自录语音的简讯 <br>\n日期时钟 <br>\n内置振动 <br>\n可选铃声 <br>\n和弦铃声[多重和弦] <br>\n自编铃声 <br>\n无线下载功能[下载铃声、游戏] <br>\n经典的益智游戏，消费者可从索尼爱立信的网站（http://wap.sonyericsson.com）下载各种最新游戏，让手机游戏内容更为丰富 <br>\n通话时间提示 <br>\n话机电话簿可存储电话 <br>\n可依来电者身分设定不同铃声，可轻易辨别来电者的身份 <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\n内置游戏[4个]：蜈蚣游戏、飞弹导航、星际争霸、突破重围 <br>\nEFR、STK服务 <br>\n闹钟 <br>\n日历 <br>\n计算器 <br>\n<br>\n配置：一锂电、一直充、摄像头<br>\n<br>\n<br>\n<br>\nT300拥有许多娱乐性很强的多媒体功能：如数码影像、短消息以及游戏等，价格定位大众市场。这部手机支持三频GSM和GPRS网络、彩屏、和弦立体声铃声以及多媒体短消息，是一部全功能的短消息手机。 <br>\n<br>\n各种功能的组和，多媒体短消息、和弦立体声铃声、可更换面板、游戏、多种影像功能以及一个256色的彩色大屏幕。这些都使T300在同样价格范围内的手机中显得与众不同。 <br>\n<br>\n拥有T300，用户可以发送各种形式的手机短信：不仅包括传统的SMS和画音短信(EMS)，还包括多媒体短信(MMS)和电子邮件。多媒体短信是广受欢迎的文字短信更进一步的技术，其特点是用户可以编写、发送和接收包括高质量的图片、格式化的文字以及声音文件在内的手机短信。T300具有24音调的和弦立体声音乐功能，可以同时奏出多个音符，使铃声品质更高、象真正的歌曲一样悦耳。T300的用户们可以共享所有的铃声信号，从互联网上下载，或自编铃声。同时，用户还可以把其它音乐录下来作为手机铃声使用，或者把不同的音乐加入个人通讯信息中，以方便地识别来电。 <br>\n<br>\nT300游戏功能包括内置的经典游戏Atari，以及从wap.sonyericsson.com网站下载各类游戏。同时，移动通讯服务供应商也可以通过他们自己的门户网给消费者提供各类品牌游戏，如Atari经典的&quot;蜈蚣&quot;、&quot;导弹指令&quot;、&quot;星&quot;、&quot;突围&quot;及其它游戏的下载服务。游戏软件开发包也可以帮助游戏开发者为T300开发更多有趣的游戏。软件开发包可以从爱立信移动世界或登陆www.mophun.com获取。 <br>\n<br>\nT300型手机将以其简洁、小巧的设计在各类手机中脱颖而出。其设计不再专注于一个复杂的表面，简洁而又先进的表面造型旨在将人们的视觉注意力集中到内部令人激动的全新技术上，比如彩色大屏幕以及单指操控键等。 <br>\n<br>\n由不同颜色组成的可更换面板也使T300显得更为人性化。T300的设计理念着重其实用价值，可以持续进行6-7.5小时的通话，并有300-500小时的待机时间(视乎当地网络情况而定)。", "0", "76", "2002-12-08 00:00:00", "pic/t300.jpg", "不详", "2980", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "True", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("205", "33", "4", "索尼爱立信T68i", "2380", "索尼+爱立信联手打造超强彩屏新机,本站超低价格销售!&amp;nbsp;", "网络类型：GSM900/1800/1900MHz,GPRS <br>\n重量：85 克 <br>\n尺寸/体积：100×48×20mm <br>\n通话时间：720分钟 * <br>\n待机时间：390小时 * <br>\n支持语言：中文简体 <br>\n<br>\n<br>\n【主要功能】 <br>\n<br>\n自动三频 <br>\nGPRS无线高速上网，最高可达64200bps(class 4, 3+1 timeslot) <br>\n支持GPRS[附带软件可使电脑通过GPRS高速接入Internet] <br>\n内置天线 <br>\n显示屏幕：屏幕大小34×28mm，解析度为101×80 pixels <br>\n彩色屏幕： 256色高对比清晰度LCD显示屏 <br>\n内置天线 <br>\nWAP 版本： 2.0、支持HSCSD、支持MMS <br>\nWTLS class 1/2/3和signText security <br>\n支持SMS，EMS及MMS <br>\nPOP3、IMAP4、和 SMTP 电子邮件用户端 <br>\n中文输入[拼音、笔划输入] <br>\n中文短信息收发 <br>\nMMS多媒体短消息发送可传图片及声音 <br>\nMMS 模板 <br>\n画音短信(EMS):用于发送和接收图片及铃声信号 <br>\n移动聊天功能 <br>\n日期时钟 <br>\n内置振动 <br>\n可选铃声 <br>\n情景模式 <br>\n通话时间提示 <br>\n话机电话簿可存储[510个]电话(+SIM卡) <br>\n图片电话簿 <br>\n图片库 <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\n动画屏保［55款］ <br>\n内置游戏[9个] <br>\n红外线接口 <br>\n内置Modem <br>\nEFR、STK服务 <br>\n闹钟 <br>\n日历:预设行程事务， 可按日、周、月显示日程编排， 同时备有提示功能 <br>\n计算器 <br>\n秒表 <br>\n记事本 <br>\n备忘录 <br>\n个人相簿，电子名片 <br>\n通过RS232传输线可同步和电脑做资料更新 <br>\n支持XHTML语言 <br>\n支持MPEG-4格式的动态影像 <br>\n支持WBMP、GIF、JPEG等图片格式 <br>\n导航控制杆，图像桌布 <br>\n无线传送属安全协定等级三 WTLS Class 3 <br>\n内置蓝牙 <br>\n收发E-Mail <br>\nSony Ericsson CommuniCam&amp;#8482; MCA20： <br>\n尺寸：46.5×48×25mm <br>\n重量：22克 <br>\n影像格式：JPEG <br>\n色彩浓度：24图点 <br>\n影像尺寸： QQQVGA (80×60像素)、QQVGA (160×120像素)、QVGA(320×240像素)、VGA (640×480像素) <br>\n最大可以保存14张640x480像素的图片，或是200张80x60像素的图片 <br>\n<br>\n配置：一锂电、一直充、线据线、光盘<br>\n<br>\n<br>\n<br>\n移动影像势将改变人们的互动方式，更将改变人们使用手机的习惯。移动影像已成为业界的热门话题，而 Sony Ericsson 将以一系列的彩色影像产品 (如 T68i 和 CommuniCam) 来确立其在此一领域的领导地位。搭配 CommuniCam MCA-20 相机使用 T68i 手机，使用者可以轻松拍照，并将相片当做 MMS 讯息传送至另一支手机，将相片当做电子邮件传送至 PC，或将相片储存在手机本身的超大容量相片簿里。 <br>\n<br>\nT68i 手机是以成功的 T68 为基础。T68 自从去年底推出以来，已在全球市场获得成功。T68i 拥有崭新的外表，是市场上第一款具 MMS 功能手机，其功能重点在于影像。许多人都在皮夹中带着朋友、家人、和宠物的相片。也有许多人将数字影像储存在计算机中。现在 T68i 可以轻松满足这两项需求，因为 T68i 可以将影像储存在手机本身的相片簿中。内建的蓝芽无线技术进一步强化了 T68i 的影像功能，因为这项技术使得 T68i 可以将影像传送至另一具手机，并以无线方式连结耳机、PC、或其它蓝芽外围设备。T68i 自然也可以从网际网络或其它来源下载彩色影像、漫画、和图像至手机。 <br>\n<br>\nSony Ericsson T68i 其它功能： <br>\n<br>\n● 三频GSM (900/1800/1900)、GPRS、HSCSD <br>\n● WAP 2.0 浏览器、WTLS class 1/2/3、signText安全性 <br>\n● POP3、IMAP4、和 SMTP 电子邮件客户端 <br>\n● SMS、EMS、和 MMS 等各种简讯格式 <br>\n<br>\nSony Ericsson CommuniCam&amp;#8482; MCA20： <br>\n● 46.5 x 48 x 25MM、22克 <br>\n● 影像格式：JPEG；色彩浓度：24图点 <br>\n● 影像尺寸： QQQVGA (80 x 60像素)、QQVGA (160 x 120 像素)、QVGA (320 x 240 像素)、VGA (640 x 480 像素)", "0", "0", "2002-12-08 00:00:00", "pic/t68i.gif", "不详", "2380", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("206", "33", "4", "索尼爱立信T100", "1450", "[国内尚未上市，买上一个，独一无二]&nbsp;-&nbsp;超薄超酷Ｔ１００", "国内尚未上市，买上一个，独一无二 <br>\n<br>\n送礼送手机绝对是首选，够特别，朋友见肯定一脸惊喜：这是一部有钱不一定能买得到的“精品”；，带着它逛街肯定让您成为注目焦点 <br>\n<br>\n<br>\n<br>\n尺寸 99X43.5X17.7mm <br>\n<br>\n重量 75克 <br>\n<br>\n中文支持： 简体中文 <br>\n<br>\n电池 730 mAh锂电 <br>\n<br>\nWAP 内置1.2.1版本WAP浏览器 <br>\n<br>\nEMS 支持EMS增强型短信息功能 <br>\n<br>\n图片动画 内置30张EMS图片，15组动画，20组图片 <br>\n<br>\n下载功能 通过WAP可以下载图片或LOGO到手机里进行编辑 振铃 内置振铃提示 <br>\n<br>\n铃声 内置10组铃声，自编10组 <br>\n<br>\n输入法 最新预测式输入法 <br>\n<br>\n个人助理 内置图片存储器、闹钟、屏幕保护、个人行事安排、日历和游戏 <br>\n<br>\n原装配置：索尼爱立信原包装盒、一电一充、说明书及相关手册。<br>\n<br>\n<br>\n<br>\n索尼爱立信最新超薄手机T100被认为是2002年年底最受欢迎机型之一，外型设计酷似电视遥控器，主要传达最新设计质感，机身两侧有彩壳边条，方便抓取手机而不划落；略微方正的造型，流露简单的复古美感；轻薄的机身，则是源自T28系列的优良传承，锁定ｅ世代新人类为目标族群。 <br>\n<br>\n挟著欧系手机极简风格，同时融入日系手机注重细节的贴心，在国外针对T100进行试用调查，根据使用者第一观感，大多数认为这款手机『造型精美』，甚至是『物超所值』。索尼爱立信因此满怀信心地认为T100将有浓厚机会脱颖而出，成为中低档手机市场的最大赢家之一。 『新视觉系』手机 极简的风格完全颠倒时下强调华丽、眩彩的设计模式，清新的面板设计，搭配蓝色冷光荧幕。 T100功能简洁实用为主 索尼爱立信 T100具备EMS音画短信、最新预测式输入法，让您轻松成为短信快手，运用图像编辑功能，创造个人独有图片电话薄，更是发挥个人创意的最佳利器。T100内建30张EMS简讯图片、15组动画及10组铃声，另可存储20组图片及10组铃声。透过WAP 1.2.1无线上网，下载图片、铃声通行无阻。再加上眩眼的蓝色背光，在光线不足的场合也可轻松使用手机。", "0", "0", "2002-12-08 00:00:00", "pic/t100.jpg", "不详", "1450", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("207", "34", "4", "飞利浦Fisio&nbsp;820", "1800", "亮丽出色最新型", "网络类型：GSM，GPRS <br>\n重量：85 克 <br>\n尺寸/体积：98×47×21mm/77cc <br>\n通话时间：120-300分钟 * <br>\n待机时间：100-300小时 * <br>\n<br>\n【主要功能】 <br>\n<br>\n彩色屏幕，大幅色彩艳丽的图片显示，将强烈冲击您的视觉 <br>\n内置天线 <br>\n自动双频 <br>\n四向导航键结合在数字键里，2、4、6、8分别是上、下、左、右键；而5就是确认键 <br>\n屏幕显示：256 颜色，9行显示,120×120个图素 <br>\n支持GPRS、Bluetooth（蓝牙），无线高速数据传送，使您体验最新超前科技带来的无穷乐趣 <br>\n数据传送时间最长达2小时（WAP over GPRS）* <br>\n可以存储三个电子邮件用帐户 (使用POP3 和 SMTP 标准) <br>\n提供约500KB的记忆空间，供储存电邮、铃声或图象用 <br>\n无线传输通讯协定，WAP 1.2.1 <br>\n中文输入[T9文字输入法] <br>\n中文短信息收发 <br>\n日期时钟 <br>\n内置振动 <br>\n录音[30秒] <br>\n语音拨号[40个] <br>\n语音接听 <br>\n声控指令 <br>\n可选铃声[19种] <br>\n私人铃声[4种] <br>\n通话时间提示 <br>\n话机电话簿可存储[300个]电话 <br>\n每个电话簿可以存5个号码和1个电子邮件地址)最大可达1800条目 <br>\n内置500kB可调整的动态记忆配置的随机存取储存器内存 <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\nCarusel 菜单 <br>\nAMR语音提升技术(比EFR更)及5个声频设定 <br>\n万用串列总线（USB）或直接使用bluetoth（蓝牙）技术与计算机同步相连 <br>\n存储下载的JPEG格式图片 <br>\n手机图片簿内置7种图片 <br>\nSTN技术彩屏，112*112像素（9行） <br>\n独特十字导航键 <br>\n情景模式包括自设状态、无音状态、省电状态、耳机状态、乘车状态、室外状态、会议 <br>\n音频均衡器 <br>\n个性化自编动画屏保，使用下载的数码照片，创建只属于您的空间 <br>\n内置Modem <br>\nEFR、STK服务 <br>\n关机状态下闹钟唤醒功能 <br>\n快速拨号 <br>\n记事本（包括假期、会议、备忘录） <br>\n计算器 <br>\n农历节日 <br>\n键盘上锁 <br>\n安全号码 <br>\n可改手机昵称（即写自己的姓名或其它） <br>\n内置277kB可调整的动态记忆配置的随机存取储存器内存 <br>\n<br>\n配置：一锂电、一直充<br>\n<br>\n<br>\n<br>\n飞利浦在外观及功能上都亮丽出色的最新型 GPRS 手机 Fisio 820，一体成型的整体设计，内建天线，重量仅 85 克，握感轻盈。 Fisio 820 共有三种颜色，其中白昼蓝、碧玉金是使用高密度烤漆，而火星红则是市场上首次推出近似珐琅质漆器的特别烤漆处理，且机身正反面皆为同色烤漆，抢眼的造型让人眼睛一亮。 <br>\n<br>\n下半年起，彩色屏幕手机在换机市场的比例将愈来愈重，Fisio 820 的彩色屏幕为 256 色(112*112 pixel画素)，分辨率高，共可显示九行英文(五行中文)，而创新与数字键结合的遥控十字键，让消费者在操控上更为便利。 <br>\n<br>\n此外，Fisio 820 在手机中加入了个人屏幕化的设计，只要将制作好的画面以图形格式 (Jpeg) 储存到手机上，即可进行图形设计或修改，并可传送及下载彩色屏幕保护画面及个人桌面，充分将个人化的特质表露无遗。Fisio 820 独特的外观设计、风潮时尚的色彩，及个人化的彩色屏幕保护及桌面，绝对是一款让人随时随地想要拿出来秀的手机，满足一般大众爱现的心理。 <br>\n<br>\n待机及通话时间长久是飞利浦手机的一大特色，免除因为电力不足而漏接重要电话的疑虑。同时，Fisio 820 一样拥有飞利浦手机长期以来标榜的声控功能，只要预录完成手机中启动上网的声控指令，便可以行使声控指令进入首页浏览资料，Fisio 820 共可储存 40 组声控拨号指令，同时设有单键上网的功能，只要依照手机屏幕上显示的「WAP」键操作，即可进入 WAP 首页，使用介面的贴心设计，可说是最人性化的手机。此外，Fisio 820 长达 30 秒的录音时间，在使用上更添便利。", "0", "0", "2002-12-08 00:00:00", "pic/820.jpg", "不详", "1800", "2350", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("208", "34", "4", "索尼Z18", "1098", "“超级小旋风”索尼Ｚ１８", "网络类型：GSM900MHz/DCS1800MHz <br>\n重量：82 克 <br>\n尺寸/体积：88×49×21.5mm/78cc <br>\n通话时间：90-220分钟 * <br>\n待机时间：30-150小时 * <br>\n<br>\n<br>\n【主要功能】 <br>\n<br>\n自动双频 <br>\n带翻盖 <br>\n中文输入[拼音联想键盘输入方式] <br>\n中文短信息收发 <br>\n日期时钟 <br>\n内置振动 <br>\n录音[200秒]：双向 <br>\n留言应答：用户在不便接听电话时内置留言或应答来电 <br>\n可选铃声[27种] <br>\n自编铃声[3种] <br>\n来电识别：区分不同电话簿分组 <br>\n通话时间提示 <br>\n话机电话簿可存储[500条]信息：每条可以输入3个电话、2条个人信息 <br>\n通话记录[10个已接、10个未接、10个已拨电话号码并带时间] <br>\n内置游戏[2种] <br>\n闹钟 <br>\n日历：可编辑日程管理 <br>\n计算器 <br>\n货币换算 <br>\n记事本 <br>\n世界时钟：18个世界重要城市时间、区号显示 <br>\nEFR服务 <br>\n全新五维操作选钮：一转一点迅速实现所需操作 <br>\n<br>\n配置：二电一充<br>\n<br>\n<br>\n<br>\n索尼“超级小旋风”――CMD-Z18，一改C8和CD18的绵长形象，时尚感极强（酷似收音机哦！）。加上冷冷的银金属色调（索尼最新推出了金色Z18），和C8相比，Z18真是换了一副身材见人。 <br>\n<br>\nZ18的外型不但小，还很时尚。在顶部的听话器、机身的四侧和天线却采用流行的深色作为点缀。 <br>\n　　 <br>\nZ18的屏幕很大，画面的显示很精致，因为它有4度灰阶显示（一般手机为黑白显示），加上它转动时的那种感觉，很有立体感，看起来就很舒服，可以支持六行中文显示，让人感到十分的亲切。而且各项操作也会因此变得更加得心应手。Z18的翻盖设计也很巧妙，闭合时，翻盖遮住了一半的屏幕，有点&quot;犹抱琵琶半遮面&quot;的感觉。 <br>\n<br>\n真的很小，拿到手上时，觉得远比想像中的小多了，Sony说这只手机是“名片尺寸”，真的拿一张名片来比比看，发现其实它不算天线的话，机身是比名片又稍微小了些，而且又只有82克的重量，真的感觉得精致、小巧。在外表的表层处理，采用UV涂层（防紫外线外壳UV Coating），没有像某些手机那样的耀眼。特别值得一提的是，Z18拿在手里，不会留下手指摸痕。索尼宣称，Z18不但不留手指摸痕，而且连作记号、刮伤都没关系。", "0", "0", "2002-12-08 00:00:00", "pic/z18.gif", "不详", "1098", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("210", "34", "4", "英资达OKWAP166", "2830", "拥有大型彩色萤幕的OKWAP166，166不但延续了OKWAPi66&amp;nbsp;引领风潮的镁铝合金外壳与折叠造型，并且将i66最受欢迎的和弦铃声升级成为十六和弦，让音色更为丰富多变，再加上消费者颇为期待的内外双萤幕设计，使得这支手机成为今年最受瞩目的热卖焦点。", "频率：GSM中文双频　　 <br>\r\n<br>\r\n尺寸：87.4x47x25.5mm（不含天线）　 <br>\r\n<br>\r\n重量：120g　 <br>\r\n<br>\r\n通话时间：50~150小时 　 <br>\r\n<br>\r\n待机时间：2.5~5小时　 <br>\r\n<br>\r\n屏幕：4096色;7色背光　　 <br>\r\n<br>\r\n电池：600mAh锂电池 <br>\r\n<br>\r\n双语词典：超过10万字　 <br>\r\n<br>\r\n铃声：16和弦　　 <br>\r\n<br>\r\n语言支持：繁体中文 <br>\r\n<br>\r\n<br>\r\n・4096色彩色大萤幕：120*160 pixels，可显示 8 行 56个中文字　　 <br>\r\n<br>\r\n・七色来电背光：钻石蓝、芙蓉粉、玛瑙红、翡翠绿、琥珀橙、宝石蓝、水晶紫’等七种不同来电分分组彩灯颜色 <br>\r\n<br>\r\n・16和弦铃声：内建52首、可下载45首、自编5首 <br>\r\n<br>\r\n・30幅个人图库：可设定为来电大头贴、桌布、开机画面及来电显示等　 <br>\r\n<br>\r\n・GPRS：支援Class 8 ( 4+1)高速上网　　 <br>\r\n<br>\r\n・EMS多媒体简讯：可互传‘文字+铃声+动画图案’的多媒体简讯　　 <br>\r\n<br>\r\n・空中书城： 提供图书下载及星座相对论等服务　 <br>\r\n<br>\r\n・E-Mail：支援POP3、SMTP无线收发邮件，并可设定24个寄件群组　 <br>\r\n<br>\r\n・语音服务：语音记事(20秒*3)、语音拨号、语音控制 <br>\r\n<br>\r\n・来电警卫：可设定各十组的拒接电话或只接电话 <br>\r\n<br>\r\n・免持扩音：免用耳机即可对话，还能多方同时通话 <br>\r\n<br>\r\n・电子辞典：10万字英汉/汉英双语辞典 <br>\r\n<br>\r\n・彩色游戏：包含乐透彩、孔明棋及可自选图形的拼图等共十二种 <br>\r\n<br>\r\n・可支援NOKIA格式的待机图形、图文简讯及铃声等 <br>\r\n<br>\r\n・500笔电话簿、500笔行事历、500笔记事簿、计算器、世界时钟、单位换算、计时等 <br>\r\n<br>\r\n<br>\r\n原装配置：一锂电池、一个直充电器、数据线、光盘、说明书、包装<br>\r\n<br>\r\n<br>\r\n<br>\r\n拥有大型彩色萤幕的OKWAP166，166不但延续了OKWAPi66 引领风潮的镁铝合金外壳与折叠造型，并且将i66最受欢迎的和弦铃声升级成为十六和弦，让音色更为丰富多变，再加上消费者颇为期待的内外双萤幕设计，使得这支手机成为今年最受瞩目的热卖焦点。 <br>\r\n<br>\r\n　　 <br>\r\n由手机名称来看，OKWAP166除了表示是延用OKWAPi66的升级版，如果把166这个数字拆开来看，前两位数字16代表着16和弦音，后两位数字66代表着双萤幕(因为6有点像眼睛)。在功能部份，从目前市面上热卖的手机机型来分析，不难发现全部都拥有折叠设计、和弦铃声、内外双萤幕、彩色大萤幕、GPRS功能等特色，但是目前只有OKWAP166才是唯一一部集这些热卖特色于一身的手机。 <br>\r\n<br>\r\n　　 <br>\r\n而功能方面也很有特色，内建52首流行歌曲及经典民谣，并可自行下载45首及自编5首铃声。OKWAP166不但拒绝传统与黑白，全新色彩色大萤幕，宽阔的浏览视野，挑逗你的眼，满足所有无限想像。彩色来电大头贴，让你一看就知谁来电，可随时WAP下载图片或上网下载经由电脑同步传输。双幕设计，可秀出来电显示、现在时间以及动画图案。另外，还有微笑圆弧来电彩灯，七种缤纷的颜色，每次铃声响起，都能为你带来好心情。内建个人图库，还可随时下载喜欢的图片。可当来电大头贴、来电分类图片、桌布、开机动画，为你的手机变妆换心情。可随时WAP无线下载图片，或上网下载经由电脑同步传输。同时传送“文字＋和弦铃声＋动画图案”，的EMS多媒体简讯，使简讯传递更生动活泼。", "0", "0", "2002-12-08 00:00:00", "pic/OKWAP166.gif", "不详", "2800", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("211", "34", "4", "索尼Z28", "1260", "Sony&nbsp;CMD-Z28是&nbsp;Z18&nbsp;的改良版本。全外壳采用时下流行的亮银色与磨光材质，配合&nbsp;96X72&nbsp;像素的高解晰大屏幕加上橘色灯光，即刻为你提升个人品味级数。Sony&nbsp;CMD-Z28&nbsp;于功能方面更以胜人一筹姿态进军场，包括新增飞碟高尔夫，二十一点，打鼹鼠等三款游戏，全新铃声以及上网“画面储存”功能，为你崇高品味的生活带来精彩点缀。", "网络类型：GSM900/1800MHz <br>\n重量：82 克 <br>\n尺寸/体积：88.1×47.6×18.0mm/80cc <br>\n通话时间：90-220分钟 <br>\n待机时间：30-150小时 <br>\n配置：二电一充 <br>\n<br>\n【主要功能】 <br>\n<br>\n全新五维操作的Advanced Jog Dial <br>\n圆弧机身线条，简洁而优雅 <br>\n琥珀色屏幕背景灯光 <br>\n四层灰阶解像度(96x72 像素) <br>\n亮银色机身，风采出众 <br>\n独特按键设计，输入方便自如 <br>\n自动双频 <br>\n开关摺合式翻盖，独有UV保护层 <br>\n中文输入[T9英文、中文拼音及笔划输入法] <br>\n中文短信息收发 <br>\n日期时钟 <br>\n内置振动 <br>\n录音[10*20秒秒] <br>\n留言应答:内置留言信箱储存40秒提示信息及10 个留言信息 <br>\n可选铃声[31种] <br>\n自编铃声[3种] <br>\n来电识别：可编定多达10 个组别，并个别设有图标及铃声 <br>\n通话时间提示 <br>\n简单快捷拨打电话 <br>\n一转一点，迅速实现所需操作，沟通世界快人一步 <br>\n静音礼貌模式选择 <br>\n话机电话簿可存储[500个]电话 <br>\n同一记录还可储存3个号码、2条个人讯息 <br>\n查询方便，更可针对记录进行分组整理，随时查知所需讯息 <br>\n通话记录[10个]已接、[10个]未接、[10个]已拨电话 <br>\n内置游戏[4个]:尔夫、打地鼠、打飞碟和21点 <br>\nEFR服务 <br>\n闹钟 <br>\n日历及日程表功能，提示重要约会和日子 <br>\n计算器 <br>\n记事本 <br>\n世界时钟(包括 IDD国家区号)功能 <br>\n货币换算 <br>\n可以接收小区广播 <br>\n具有接收电子邮件并传真到用户的传真机上的功能<br>\n<br>\n<br>\n<br>\nSony CMD-Z28是 Z18 的改良版本。全外壳采用时下流行的亮银色与磨光材质，配合 96X72 像素的高解晰大屏幕加上橘色灯光，即刻为你提升个人品味级数。Sony CMD-Z28 于功能方面更以胜人一筹姿态进军场，包括新增飞碟高尔夫，二十一点，打鼹鼠等三款游戏，全新铃声以及上网“画面储存”功能，为你崇高品味的生活带来精彩点缀。 <br>\n<br>\nSony CMD-Z28 最独特的功能莫过于“画面储存”功能了！您可以用于储存游戏、Email、WAP 及 Web 的画面，只要按下手机的“Memo”钮，就可以将屏幕画面储存于“话音便条”中的“画面储存”选项里，对于重要的网络或是 Email 不用上网也能读取，是一项方便，经济又快速的功能。 <br>\nZ28 保有 Z18 绝大部分的主要功能，包括话机左上角的 Jog Dial 五方向飞梭滚轮键的快速选单设计、房车级 UV 烤漆、4级灰阶液晶屏幕，可显示五行中文、内建的 31 组来电铃声，以及 3 组自编铃声、内建录音机，提供约 200 秒的录音容量(10 个 20 秒)、500 组电话簿，可分多达 10 个组别，并个别设有图标及铃声、以及自订行程表、世界时间、世界货币换算 (有世界地图接口)、闹钟、来电震动、听声辨人等强大功能。", "0", "0", "2002-12-08 00:00:00", "pic/z28.gif", "不详", "1260", "0", "普通", "不详", "", "100", "2002-10-10 00:00:0