INSERT INTO hw (hw_id, sort_id, nsort_id, hw_name, hw_cash, hw_content, hw_content2, hw_views, hw_buys, hw_date, hw_pic, chubsh, daili, pifa, zhuang, hw_sn, company, yeshu, data, kaiben, isbn, tuijian, jia) VALUES("212", "34", "4", "雷特", "1388", "Ｖ７０的兄弟雷特Ｖ８００", "功能： <br>\n<br>\n4和弦铃声 <br>\n<br>\n动画桌面 <br>\n<br>\nWAP上网 <br>\n<br>\n声控拨号 <br>\n<br>\n免提通话 <br>\n<br>\n世界时间 <br>\n<br>\n超蓝屏幕 <br>\n<br>\n三组游戏 <br>\n<br>\n200组电话记录 <br>\n<br>\n原装配置:一锂电、一直充、说明书<br>\n<br>\n<br>\n<br>\n自摩托罗拉公司于年初在国内市场正式推出V70后,。凭借超越时尚、独具匠心的设计风格， V70一亮相就征服了许多玩机族和时尚人士的心，加上接连的市场炒作活动，漫天飞的户外大广告和电视广告轰炸，明星轮番做SHOW，限量发烧珍藏版活动，MOTOROLA在短短的一个月内，用V70这一款机型就完全压制住了NOKIA的多款新机型轮番冲击市场的火热。 <br>\n　　 <br>\n一般说来，现在人们在选择一款手机时，除了实用的功能外，也越来越看重外型了，特别那些Fashion一族。而V70正是迎合了这类购买群的消费心理，在酷和眩上下足了工夫，一副冷若冰霜，拘人于千里之外的样子，360度旋转，通体采用摩沙金属材料，透明的白色塑料内饰，超强对比的屏幕显示（黑色衬托，白色字体），就机形而言，绝对可以说能让我们过目不忘。也许，正如我们常说的&quot;树大招风&quot;，细心的朋友会发现最近出现了一款新机型-V800，单就它的命名上我们很容易把它归为MOTO的V系列，而最近一些媒体也错误地把它当做V70的孪生，事实上并不是这样，这款手机乃是台湾一知名的的手机设计商NETT <br>\n<br>\nV70与V800对比图 <br>\n<br>\n我们可以清晰地看到二者在外观上大同小异，V800相对体形臃肿，但较宽的屏幕能支持3行文字。V800采用鱼旗式的设计，这样的设计相信能增强信号的接受。 <br>\n新增加的蓝色背景灯是V800区别与在强光的映射下，V800表现出的强质感让我们为之心动。", "0", "0", "2002-12-08 00:00:00", "pic/v800.jpg", "不详", "1388", "0", "普通", "不详", "", "100", "2002-10-10 00:00:00", "16", "不详", "False", "False");
